INSERT INTO `onethink_music` VALUES (51, '每个人都会', '', 239, '[00:02.140]爱是两人一张棉被\r\n[00:05.300]也是一人一瓶清水 都说对\r\n[00:11.850]爱是送一百枝玫瑰\r\n[00:14.830]也是让湿地开花蕊 就OK\r\n[00:21.080]人间的青草地 需要浇水\r\n[00:25.680]内心的花园就不会枯萎\r\n[00:30.620]把最甜最好的滋味\r\n[00:33.580]散播到东南西北\r\n[00:40.320]什麽比love love love love love更美\r\n[00:46.440]爱让人安心在梦中熟睡\r\n[00:50.750]Oh love love love love love\r\n[00:54.620]最美\r\n[00:56.130]美在每一个人都会\r\n[01:03.010]爱是让人感动流泪\r\n[01:05.820]也是停止制造伤悲 都答对\r\n[01:12.510]爱是为好朋友解围\r\n[01:15.550]也是为陌生人破费 就OK\r\n[01:21.680]人间的青草地 需要浇水\r\n[01:26.430]内心的花园就不会枯萎\r\n[01:31.360]把最甜最好的滋味\r\n[01:34.280]散播到东南西北\r\n[01:40.250]\r\n[01:40.970]什麽比love love love love love更美\r\n[01:47.180]爱让人安心在梦中熟睡\r\n[01:51.360]Oh love love love love love\r\n[01:55.270]最美\r\n[01:56.750]美在每一个人都会\r\n[02:02.360]爱肯付出的汗水\r\n[02:04.530]爱小王子的蔷薇\r\n[02:07.070]爱旅途上的兄弟姐妹\r\n[02:11.920]爱所有青山绿水\r\n[02:14.220]爱所有难忘约会\r\n[02:16.800]爱上对爱的体会\r\n[02:20.260]只有love love love love love最美\r\n[02:26.030]她会变成最安全的堡垒\r\n[02:30.120]什麽比love love love love\r\n[02:33.350]love更美\r\n[02:35.740]美在只要用心都会学会\r\n[02:40.630]不用什麽只为 只要用心体会\r\n[02:45.460]爱让每人都有机会\r\n', '', '方大同', '', 240, 0, 0, 1, 0, '2019-06-13 08:08:59', 1);
