INSERT INTO `onethink_music` VALUES (65, '奇妙能力歌', '', 129, '[00:01.000] 作词:陈粒\r\n[00:02.000] 作曲:陈粒\r\n[00:10.312]编曲：燕池\r\n[00:20.312]我看过沙漠下暴雨\r\n[00:24.862]看过大海亲吻鲨鱼\r\n[00:29.113]看过黄昏追逐黎明\r\n[00:32.263]没看过你\r\n[00:37.664]我知道美丽会老去\r\n[00:41.765]生命之外还有生命\r\n[00:46.266]我知道风里有诗句\r\n[00:49.617]不知道你\r\n[00:55.169]我听过荒芜变成热闹\r\n[00:59.519]听过尘埃掩埋城堡\r\n[01:03.869]听过天空拒绝飞鸟\r\n[01:07.170]没听过你\r\n[01:12.621]我明白眼前都是气泡\r\n[01:16.721]安静的才是苦口良药\r\n[01:21.272]明白什么才让我骄傲\r\n[01:24.472]不明白你\r\n[01:30.273]我拒绝更好更圆的月亮\r\n[01:34.573]拒绝未知的疯狂\r\n[01:38.923]拒绝声色的张扬\r\n[01:42.240]不拒绝你\r\n[01:47.574]我变成荒凉的景象\r\n[01:51.976]变成无所谓的模样\r\n[01:55.777]变成透明的高墙\r\n[01:59.528]没能变成你\r\n[02:07.428]\r\n[02:39.996]我听过空境的回忆\r\n[02:44.496]雨水浇绿孤山岭\r\n[02:48.746]听过被诅咒的秘密\r\n[02:52.490]没听过你\r\n[02:57.299]我抓住散落的欲望\r\n[03:01.649]缱绻的馥郁让我紧张\r\n[03:05.949]我抓住时间的假想\r\n[03:09.200]没抓住你\r\n[03:14.650]我包容六月清泉结冰\r\n[03:19.187]包容暮老的生命\r\n[03:23.489]包容世界的迟疑\r\n[03:26.789]没包容你\r\n[03:32.139]我忘了置身濒绝孤岛\r\n[03:36.640]忘了眼泪不过失效药\r\n[03:40.990]忘了百年无声口号\r\n[03:44.490]没能忘记你\r\n[03:49.791]我想要更好更圆的月亮\r\n[03:53.742]想要未知的疯狂\r\n[03:58.592]想要声色的张扬\r\n[04:02.142]我想要你\r\n', '', '陈粒', '', 130, 0, 0, 1, 0, '2020-01-07 11:01:56', 1);
INSERT INTO `onethink_music` VALUES (66, '奇妙能力歌（法语版）（Cover 陈粒）', '', 131, '[00:35.64]J\'ai vu qu\'il pleuvait dans le désert\r\n[00:39.94]J\'ai vu un requin baissé par la mer\r\n[00:44.29]J\'ai vu la nuit poursuivre l\'aurore\r\n[00:48.02]Je ne t\'ai vue jamais\r\n[00:52.91]Je sais la beauté pourra disparaître\r\n[00:57.00]La vie marche avant sans arrêt\r\n[01:01.28]Je saurais les poésies sous le vent\r\n[01:04.74]Je ne saurais te connaître\r\n[01:10.21]J\'entends le tapage envahir le silence\r\n[01:14.37]Le chateau enseveli sous poussières\r\n[01:18.80]J\'entends le ciel empêcher les oiseaux\r\n[01:21.82]Je ne t\'entends jamais\r\n[01:27.32]J\'ai vu les écumes devant mes yeux\r\n[01:31.78]La sérénité est le meilleur remède\r\n[01:35.89]J\'ai su ce qui me faisait si fier\r\n[01:39.21]Je ne t\'ai comprise jamais\r\n[01:44.69]J\'ai refusé la lune qui est plus belle\r\n[01:48.98]Et la folie que je n\'aime jamais\r\n[01:53.20]Je déteste toujours de parler trop\r\n[01:57.44]Sauf de te parler\r\n[02:02.35]Je suis devenu vite un désert\r\n[02:07.00]Sans avoir pluies désirées\r\n[02:11.39]Je suis devenu un mur transparent\r\n[02:15.06]Tu ne me vois jamais\r\n[02:18.24]\r\n[02:54.48]J\'ai entendu des échos de vallée\r\n[02:59.53]La diluvienne bientôt arrivée\r\n[03:03.92]J\'ai entendu des secrets d\'être maudit\r\n[03:07.24]Je ne t\'entends jamais\r\n[03:12.22]J\'ai recueilli des désirs répandus\r\n[03:16.97]La tendresse de vie me rend énervé\r\n[03:21.11]J\'ai perçu les mirages sous le temps\r\n[03:24.83]Mais je t\'ai perdue\r\n[03:30.19]J\'accepte le neige avant juillet\r\n[03:34.63]Et la vie qui ne vielle jamais\r\n[03:38.79]J\'ai accepté l\'hésitation du monde\r\n[03:42.19]Je ne t\'ai pas acceptée\r\n[03:47.59]J\'ai oublié d\'être dans la solitude\r\n[03:51.66]Et les larmes n\'étaient pas utiles\r\n[03:55.84]J\'ai oublié des cris de cent années\r\n[03:59.70]Et enfin je t\'oublie\r\n[04:05.28]J\'ai envie de la lune qui est plus belle\r\n[04:09.28]Et la folie que j\'aime jamais\r\n[04:13.56]J\'ai très envie de parler trop\r\n[04:17.27]Surtout de te parle\r\n', '', '温晴', '', 132, 0, 0, 0, 65, '2020-01-07 11:02:15', 1);
