INSERT INTO `onethink_music` VALUES (69, 'Charlie Boy', '', 108, '[00:00.33]The Lumineers - Charlie Boy\r\n[00:02.27].\r\n[00:16.02]Charlie boy, don\'t go to war, first born in forty-four\r\n[00:30.15]Kennedy made him believe we could do much more\r\n[00:41.59].\r\n[00:45.22]Ohhh ooh ooh ooh\r\n[00:53.15]Ohhh ooh ooh ooh\r\n[01:00.47].\r\n[01:15.28]Lillian, don\'t hang your head, love should make you feel good\r\n[01:30.22]In uniform you raised a man, who volunteered to stand\r\n[01:41.28].\r\n[01:46.66]Ohhh ooh ooh ooh\r\n[01:53.03]Ohhh ooh ooh ohh\r\n[02:00.28].\r\n[02:00.53]Play the bugle, play the taps and\r\n[02:07.22]Make your mothers proud\r\n[02:15.22]Raise your rifles to the sky boys\r\n[02:22.59]Fire that volley loud\r\n[02:26.91].\r\n[02:45.05]News was bad on upland ave., metuchen mourn our loss\r\n[03:00.05]Sons rebelled, while fathers yelled, and mothers clutched the cross\r\n[03:11.93].\r\n[03:15.55]Ohhh ooh ooh ooh\r\n[03:22.24]Ohhh ooh ooh ooh\r\n[03:28.80].\r\n[03:30.30]Play the bugle, play the taps and\r\n[03:37.61]Make your mothers proud\r\n[03:44.49]Raise your rifles to the sky boys\r\n[03:52.61]Fire that volley loud\r\n', '', 'The Lumineers', '', 109, 0, 0, 1, 0, '2020-01-07 11:42:58', 1);
