INSERT INTO `onethink_plugin_cheat_sheet` VALUES (1, 'ThinkPHP 5.0 速查表', '<p>本速查表里的类都是think为命名空间的，实例化时省去了 &nbsp;use。用的时候注意。</p><p>本速查表里会有四种方法的调用：</p><p>&nbsp; &nbsp; 公有方法 $class = new Class(); &nbsp;$class-&gt;foo();<br></p><p>&nbsp; &nbsp; 公有静态 Class::foo();<br></p><p>&nbsp; &nbsp; 私有方法 $this-&gt;foo();<br></p><p>&nbsp; &nbsp; 私有静态 self::foo();<br></p><p>关于注释，为了简洁，/** 的单行注释被我改为了 //&nbsp;</p><p><br></p>', 'ThinkPHP Cheat Sheet , Codes , function , methods of laravel framework', 'yangweijie', '2017-04-22 14:05:12', '2017-04-30 22:55:28');
INSERT INTO `onethink_plugin_cheat_sheet_detail` VALUES (1, 1, '', 'App', '// 执行应用程序\r\nApp::run(Request $request = null);\r\n\r\n// 设置当前请求的调度信息\r\nApp::dispatch($dispatch, $type = \'module\');\r\n\r\n// 执行函数或者闭包方法 支持参数调用\r\nApp::invokeFunction($function, $vars = []);\r\n\r\n// 调用反射执行类的方法 支持参数绑定\r\nApp::invokeMethod($method, $vars = []);\r\n\r\n// 调用反射执行类的实例化 支持依赖注入\r\nApp::invokeClass($class, $vars = []);\r\n\r\n// 绑定参数\r\nApp::bindParams($reflect, $vars = []);\r\n\r\n// 执行模块\r\nApp::module($result, $config, $convert = null);\r\n\r\n// 初始化应用\r\nApp::initCommon();\r\n\r\n// 初始化应用或模块\r\nApp::init($module = \'\');\r\n\r\n// URL路由检测（根据PATH_INFO)\r\nApp::routeCheck($request, array $config);\r\n\r\n// 设置应用的路由检测机制\r\nApp::route($route, $must = false);', '2017-04-23 20:46:12', '2017-04-24 08:17:18');
INSERT INTO `onethink_plugin_cheat_sheet_detail` VALUES (2, 1, 'http://www.kancloud.cn/manual/thinkphp5/118021', 'Build', '// 根据传入的build资料创建目录和文件\r\nBuild::run(array $build = [], $namespace = \'app\', $suffix = false);\r\n\r\n// 创建目录\r\nBuild::buildDir($list);\r\n\r\n// 创建文件\r\nBuild::buildFile($list);\r\n\r\n// 创建模块\r\nBuild::module($module = \'\', $list = [], $namespace = \'app\', $suffix = false);\r\n\r\n// 创建模块的欢迎页面\r\nBuild::buildHello($module, $namespace, $suffix = false);\r\n\r\n// 创建模块的公共文件\r\nBuild::buildCommon($module);', '2017-04-23 21:14:10', '2017-04-30 21:07:53');
INSERT INTO `onethink_plugin_cheat_sheet_detail` VALUES (3, 1, 'http://www.kancloud.cn/manual/thinkphp5/118131', 'Cache', '// 连接缓存\r\nCache::connect(array $options = [], $name = false);\r\n\r\n// 自动初始化缓存\r\nCache::init(array $options = []);\r\n\r\n// 切换缓存类型 需要配置 cache.type 为 complex\r\nCache::store($name = \'\');\r\n\r\n// 判断缓存是否存在\r\nCache::has($name);\r\n\r\n// 读取缓存\r\nCache::get($name, $default = false);\r\n\r\n// 写入缓存\r\nCache::set($name, $value, $expire = null);\r\n\r\n// 自增缓存（针对数值缓存）\r\nCache::inc($name, $step = 1);\r\n\r\n// 自减缓存（针对数值缓存）\r\nCache::dec($name, $step = 1);\r\n\r\n// 删除缓存\r\nCache::rm($name);\r\n\r\n// 清除缓存\r\nCache::clear($tag = null);\r\n\r\n// 读取缓存并删除\r\nCache::pull($name);\r\n\r\n// 如果不存在则写入缓存\r\nCache::remember($name, $value, $expire = null);\r\n\r\n// 缓存标签\r\nCache::tag($name, $keys = null, $overlay = false);', '2017-04-23 21:14:47', '2017-04-30 21:10:32');
INSERT INTO `onethink_plugin_cheat_sheet_detail` VALUES (4, 1, 'http://www.kancloud.cn/manual/thinkphp5/118022', 'Config', '// 解析配置文件或内容\r\nConfig::parse($config, $type = \'\', $name = \'\', $range = \'\');\r\n\r\n// 加载配置文件（PHP格式）\r\nConfig::load($file, $name = \'\', $range = \'\');\r\n\r\n// 检测配置是否存在\r\nConfig::has($name, $range = \'\');\r\n\r\n// 获取配置参数 为空则获取所有配置\r\nConfig::get($name = null, $range = \'\');\r\n\r\n// 设置配置参数 name为数组则为批量设置\r\nConfig::set($name, $value = null, $range = \'\');\r\n\r\n// 重置配置参数\r\nConfig::reset($range = \'\');', '2017-04-23 21:19:43', '2017-04-30 21:11:03');
INSERT INTO `onethink_plugin_cheat_sheet_detail` VALUES (5, 1, 'http://www.kancloud.cn/manual/thinkphp5/118047', 'Controller', '$controller = new think\\Controller();\r\n\r\n// 前置操作\r\n$controller->beforeAction($method, $options = []);\r\n\r\n// 加载模板输出\r\n$controller->fetch($template = \'\', $vars = [], $replace = [], $config = []);\r\n\r\n// 渲染内容输出\r\n$controller->display($content = \'\', $vars = [], $replace = [], $config = []);\r\n\r\n// 模板变量赋值\r\n$controller->assign($name, $value = \'\');\r\n\r\n// 初始化模板引擎\r\n$controller->engine($engine);\r\n\r\n// 设置验证失败后是否抛出异常\r\n$controller->validateFailException($fail = true);\r\n\r\n// 验证数据\r\n$controller->validate($data, $validate, $message = [], $batch = false, $callback = null);', '2017-04-23 21:20:07', '2017-04-30 21:11:35');
INSERT INTO `onethink_plugin_cheat_sheet_detail` VALUES (6, 1, 'http://www.kancloud.cn/manual/thinkphp5/122951', 'Console', 'Console::init($run = true);\r\n\r\nConsole::call($command, array $parameters = [], $driver = \'buffer\');\r\n\r\n$console = new think\\Console();\r\n// 执行当前的指令\r\n$console->run();\r\n\r\n// 执行指令\r\n$console->doRun(Input $input, Output $output);\r\n\r\n// 设置输入参数定义\r\n$console->setDefinition(InputDefinition $definition);\r\n\r\n// 获取输入参数定义\r\n$console->getDefinition();\r\n\r\n// Gets the help message.\r\n$console->getHelp();\r\n\r\n// 是否捕获异常\r\n$console->setCatchExceptions($boolean);\r\n\r\n// 是否自动退出\r\n$console->setAutoExit($boolean);\r\n\r\n// 获取名称\r\n$console->getName();\r\n\r\n// 设置名称\r\n$console->setName($name);\r\n\r\n// 获取版本\r\n$console->getVersion();\r\n\r\n// 设置版本\r\n$console->setVersion($version);\r\n\r\n// 获取完整的版本号\r\n$console->getLongVersion();\r\n\r\n// 注册一个指令\r\n$console->register($name);\r\n\r\n// 添加指令\r\n$console->addCommands(array $commands);\r\n\r\n// 添加一个指令\r\n$console->add(Command $command);\r\n\r\n// 获取指令\r\n$console->get($name);\r\n\r\n// 某个指令是设置默认命令否存在\r\n$console->has($name);\r\n\r\n// 获取所有的命名空间\r\n$console->getNamespaces();\r\n\r\n// 查找注册命名空间中的名称或缩写。\r\n$console->findNamespace($namespace);\r\n\r\n// 查找指令\r\n$console->find($name);\r\n\r\n// 获取所有的指令\r\n$console->all($namespace = null);\r\n\r\n// 获取可能的指令名\r\nConsole::getAbbreviations($names);\r\n\r\n// 配置基于用户的参数和选项的输入和输出实例。\r\n$this->configureIO(Input $input, Output $output);\r\n\r\n// 执行指令\r\n$this->doRunCommand(Command $command, Input $input, Output $output);\r\n\r\n// 获取指令的基础名称\r\n$this->getCommandName(Input $input);\r\n\r\n// 获取默认输入定义\r\n$this->getDefaultInputDefinition();\r\n\r\n// 设置默认命令\r\n$this->getDefaultCommands();\r\n\r\nConsole::addDefaultCommands(array $classnames);\r\n\r\n// 获取可能的建议\r\n$this->getAbbreviationSuggestions($abbrevs);\r\n\r\n// 返回命名空间部分\r\n$console->extractNamespace($name, $limit = null);\r\n\r\n// 查找可替代的建议\r\n$this->findAlternatives($name, $collection);\r\n\r\n// 设置默认的指令\r\n$console->setDefaultCommand($commandName);\r\n\r\n// 返回所有的命名空间\r\n$this->extractAllNamespaces($name);', '2017-04-23 21:20:34', '2017-04-30 21:12:00');
INSERT INTO `onethink_plugin_cheat_sheet_detail` VALUES (12, 1, 'http://www.kancloud.cn/manual/thinkphp5/155159', 'File', '$file = new File(\'test.txt\', \'a\');\r\n\r\n//是否测试\r\n$file->isTest($test = false);\r\n\r\n// 设置上传信息\r\n$file->setUploadInfo($info);\r\n\r\n// 获取上传文件的信息\r\n$file->getInfo($name = \'\');\r\n\r\n// 获取上传文件的文件名\r\n$file->getSaveName();\r\n\r\n// 设置上传文件的保存文件名\r\n$file->setSaveName($saveName);\r\n\r\n// 获取文件的哈希散列值\r\n$file->hash($type = \'sha1\');\r\n\r\n// 检查目录是否可写\r\n$this->checkPath($path);\r\n\r\n// 获取文件类型信息\r\n$file->getMime();\r\n\r\n// 设置文件的命名规则\r\n$file->rule($rule);\r\n\r\n// 设置上传文件的验证规则\r\n$file->validate($rule = []);\r\n\r\n// 检测是否合法的上传文件\r\n$file->isValid();\r\n\r\n// 检测上传文件\r\n$file->check($rule = []);\r\n\r\n// 检测上传文件后缀\r\n$file->checkExt($ext);\r\n\r\n// 检测图像文件\r\n$file->checkImg();\r\n\r\n// 判断图像类型\r\n$this->getImageType($image);\r\n\r\n// 检测上传文件大小\r\n$file->checkSize($size);\r\n\r\n// 检测上传文件类型\r\n$file->checkMime($mime);\r\n\r\n /**\r\n * 移动文件\r\n * @param  string           $path    保存路径\r\n * @param  string|bool      $savename    保存的文件名 默认自动生成\r\n * @param  boolean          $replace 同名文件是否覆盖\r\n * @return false|SplFileInfo false-失败 否则返回SplFileInfo实例\r\n */\r\n$file->move($path, $savename = true, $replace = true);\r\n\r\n// 获取保存文件名\r\n$this->buildSaveName($savename);\r\n\r\n// 获取错误代码信息\r\n$this->error($errorNo);\r\n\r\n// 获取错误信息\r\n$file->getError();', '2017-04-24 08:38:19', '2017-04-30 21:16:29');
INSERT INTO `onethink_plugin_cheat_sheet_detail` VALUES (7, 1, 'http://www.kancloud.cn/manual/thinkphp5/126167', 'Cookie', '// Cookie初始化\r\nCookie::init();\r\n\r\n// 设置或者获取cookie作用域（前缀）\r\nCookie::prefix($prefix = \'\');\r\n\r\n// Cookie 设置、获取、删除\r\nCookie::set($name, $value = \'\', $option = null);\r\n\r\n// 永久保存Cookie数据\r\nCookie::forever($name, $value = \'\', $option = null);\r\n\r\n// 判断Cookie数据\r\nCookie::has($name, $prefix = null);\r\n\r\n// Cookie获取\r\nCookie::get($name, $prefix = null);\r\n\r\n// Cookie删除\r\nCookie::delete($name, $prefix = null);\r\n\r\n// Cookie清空\r\nCookie::clear($prefix = null);\r\n\r\nCookie::jsonFormatProtect(&$val, $key, $type = \'encode\');', '2017-04-23 21:22:08', '2017-04-30 21:12:35');
INSERT INTO `onethink_plugin_cheat_sheet_detail` VALUES (8, 1, 'http://www.kancloud.cn/manual/thinkphp5/118058', 'Db', '// 数据库初始化 并取得数据库类实例\r\nDb::connect($config = [], $name = false);\r\n\r\n// 数据库连接参数解析\r\nDb::parseConfig($config);\r\n\r\n// DSN解析\r\n// 格式： mysql://username:passwd@localhost:3306/DbName?param1=val1¶m2=val2#utf8\r\nDb::parseDsn($dsnStr);\r\n\r\n// 调用驱动类的方法\r\nDb::__callStatic($method, $params);', '2017-04-23 21:26:10', '2017-04-30 21:13:02');
INSERT INTO `onethink_plugin_cheat_sheet_detail` VALUES (9, 1, 'http://www.kancloud.cn/manual/thinkphp5/118134', 'Debug', '// 记录时间（微秒）和内存使用情况\r\nDebug::remark($name, $value = \'\');\r\n\r\n// 统计某个区间的时间（微秒）使用情况\r\nDebug::getRangeTime($start, $end, $dec = 6);\r\n\r\n// 统计从开始到统计时的时间（微秒）使用情况\r\nDebug::getUseTime($dec = 6);\r\n\r\n// 获取当前访问的吞吐率情况\r\nDebug::getThroughputRate();\r\n\r\n// 记录区间的内存使用情况\r\nDebug::getRangeMem($start, $end, $dec = 2);\r\n\r\n// 统计从开始到统计时的内存使用情况\r\nDebug::getUseMem($dec = 2);\r\n\r\n// 统计区间的内存峰值情况\r\nDebug::getMemPeak($start, $end, $dec = 2);\r\n\r\n// 获取文件加载信息\r\nDebug::getFile($detail = false);\r\n\r\n// 浏览器友好的变量输出\r\nDebug::dump($var, $echo = true, $label = null, $flags = ENT_SUBSTITUTE);\r\n\r\nDebug::inject(Response $response, &$content);', '2017-04-23 21:26:36', '2017-04-30 21:13:22');
INSERT INTO `onethink_plugin_cheat_sheet_detail` VALUES (11, 1, 'http://www.kancloud.cn/manual/thinkphp5/126075', 'Exception', '/**\r\n * 设置异常额外的Debug数据\r\n * 数据将会显示为下面的格式\r\n *\r\n * Exception Data\r\n * --------------------------------------------------\r\n * Label 1\r\n *   key1      value1\r\n *   key2      value2\r\n * Label 2\r\n *   key1      value1\r\n *   key2      value2\r\n *\r\n * @param string $label 数据分类，用于异常页面显示\r\n * @param array  $data  需要显示的数据，必须为关联数组\r\n */\r\n$this->setData($label, array $data);\r\n\r\n/**\r\n * 获取异常额外Debug数据\r\n * 主要用于输出到异常页面便于调试\r\n * @return array 由setData设置的Debug数据\r\n */\r\n$this->getData();', '2017-04-24 08:24:40', '2017-04-30 21:15:11');
INSERT INTO `onethink_plugin_cheat_sheet_detail` VALUES (10, 1, 'http://www.kancloud.cn/manual/thinkphp5/189989', 'Env', '// 获取环境变量值\r\nEnv::get($name, $default = null);', '2017-04-23 21:27:10', '2017-04-30 21:14:17');
INSERT INTO `onethink_plugin_cheat_sheet_detail` VALUES (13, 1, 'http://www.kancloud.cn/manual/thinkphp5/118130', 'Hook', '/**\r\n * 动态添加行为扩展到某个标签\r\n * @param string    $tag 标签名称\r\n * @param mixed     $behavior 行为名称\r\n * @param bool      $first 是否放到开头执行\r\n * @return void\r\n */\r\nHook::add($tag, $behavior, $first = false);\r\n\r\n/**\r\n * 批量导入插件\r\n * @param array        $tags 插件信息\r\n * @param boolean     $recursive 是否递归合并\r\n */\r\nHook::import(array $tags, $recursive = true);\r\n\r\n /**\r\n * 获取插件信息\r\n * @param string $tag 插件位置 留空获取全部\r\n * @return array\r\n */\r\n Hook::get($tag = \'\');\r\n\r\n /**\r\n * 监听标签的行为\r\n * @param string $tag    标签名称\r\n * @param mixed  $params 传入参数\r\n * @param mixed  $extra  额外参数\r\n * @param bool   $once   只获取一个有效返回值\r\n * @return mixed\r\n */\r\nHook::listen($tag, &$params = null, $extra = null, $once = false);\r\n\r\n/**\r\n * 执行某个行为\r\n * @param mixed     $class 要执行的行为\r\n * @param string    $tag 方法名（标签名）\r\n * @param Mixed     $params 传人的参数\r\n * @param mixed     $extra 额外参数\r\n * @return mixed\r\n */\r\nHook::exec($class, $tag = \'\', &$params = null, $extra = null);', '2017-04-24 08:41:23', '2017-04-30 21:17:24');
INSERT INTO `onethink_plugin_cheat_sheet_detail` VALUES (14, 1, 'http://www.kancloud.cn/manual/thinkphp5/144731', 'Helper', '// 快速导入Traits PHP5.5以上无需调用\r\nload_trait($class, $ext = EXT);\r\n\r\n// 抛出异常处理\r\nexception($msg, $code = 0, $exception = \'\');\r\n\r\n/**\r\n * 记录时间（微秒）和内存使用情况\r\n * @param string            $start 开始标签\r\n * @param string            $end 结束标签\r\n * @param integer|string    $dec 小数位 如果是m 表示统计内存占用\r\n * @return mixed\r\n */\r\ndebug($start, $end = \'\', $dec = 6);\r\n\r\n/**\r\n * 获取语言变量值\r\n * @param string    $name 语言变量名\r\n * @param array     $vars 动态变量值\r\n * @param string    $lang 语言\r\n * @return mixed\r\n */\r\nlang($name, $vars = [], $lang = \'\');\r\n\r\n/**\r\n * 获取和设置配置参数\r\n * @param string|array  $name 参数名\r\n * @param mixed         $value 参数值\r\n * @param string        $range 作用域\r\n * @return mixed\r\n */\r\nconfig($name = \'\', $value = null, $range = \'\');\r\n\r\n/**\r\n * 获取输入数据 支持默认值和过滤\r\n * @param string    $key 获取的变量名\r\n * @param mixed     $default 默认值\r\n * @param string    $filter 过滤方法\r\n * @return mixed\r\n */\r\ninput($key = \'\', $default = null, $filter = \'\');\r\n\r\n/**\r\n * 渲染输出Widget\r\n * @param string    $name Widget名称\r\n * @param array     $data 传入的参数\r\n * @return mixed\r\n *\r\nwidget($name, $data = []);\r\n\r\n/**\r\n * 实例化Model\r\n * @param string    $name Model名称\r\n * @param string    $layer 业务层名称\r\n * @param bool      $appendSuffix 是否添加类名后缀\r\n * @return \\think\\Model\r\n */\r\nmodel($name = \'\', $layer = \'model\', $appendSuffix = false);\r\n\r\n/**\r\n * 实例化验证器\r\n * @param string    $name 验证器名称\r\n * @param string    $layer 业务层名称\r\n * @param bool      $appendSuffix 是否添加类名后缀\r\n * @return \\think\\Validate\r\n */\r\nvalidate($name = \'\', $layer = \'validate\', $appendSuffix = false);\r\n\r\n/**\r\n * 实例化数据库类\r\n * @param string        $name 操作的数据表名称（不含前缀）\r\n * @param array|string  $config 数据库配置参数\r\n * @param bool          $force 是否强制重新连接\r\n * @return \\think\\db\\Query\r\n */\r\ndb($name = \'\', $config = [], $force = true);\r\n\r\n/**\r\n * 实例化控制器 格式：[模块/]控制器\r\n * @param string    $name 资源地址\r\n * @param string    $layer 控制层名称\r\n * @param bool      $appendSuffix 是否添加类名后缀\r\n * @return \\think\\Controller\r\n */\r\ncontroller($name, $layer = \'controller\', $appendSuffix = false);\r\n\r\n/**\r\n * 调用模块的操作方法 参数格式 [模块/控制器/]操作\r\n * @param string        $url 调用地址\r\n * @param string|array  $vars 调用参数 支持字符串和数组\r\n * @param string        $layer 要调用的控制层名称\r\n * @param bool          $appendSuffix 是否添加类名后缀\r\n * @return mixed\r\n */\r\naction($url, $vars = [], $layer = \'controller\', $appendSuffix = false);\r\n\r\n/**\r\n * 导入所需的类库 同java的Import 本函数有缓存功能\r\n * @param string    $class 类库命名空间字符串\r\n * @param string    $baseUrl 起始路径\r\n * @param string    $ext 导入的文件扩展名\r\n * @return boolean\r\n */\r\nimport($class, $baseUrl = \'\', $ext = EXT);\r\n\r\n/**\r\n * 快速导入第三方框架类库 所有第三方框架的类库文件统一放到 系统的Vendor目录下面\r\n * @param string    $class 类库\r\n * @param string    $ext 类库后缀\r\n * @return boolean\r\n */\r\nvendor($class, $ext = EXT);\r\n\r\n/**\r\n * 浏览器友好的变量输出\r\n * @param mixed     $var 变量\r\n * @param boolean   $echo 是否输出 默认为true 如果为false 则返回输出字符串\r\n * @param string    $label 标签 默认为空\r\n * @return void|string\r\n */\r\ndump($var, $echo = true, $label = null);\r\n\r\n/**\r\n * Url生成\r\n * @param string        $url 路由地址\r\n * @param string|array  $vars 变量\r\n * @param bool|string   $suffix 生成的URL后缀\r\n * @param bool|string   $domain 域名\r\n * @return string\r\n */\r\nurl($url = \'\', $vars = \'\', $suffix = true, $domain = false);\r\n\r\n/**\r\n * Session管理\r\n * @param string|array  $name session名称，如果为数组表示进行session设置\r\n * @param mixed         $value session值\r\n * @param string        $prefix 前缀\r\n * @return mixed\r\n */\r\nsession($name, $value = \'\', $prefix = null);\r\n\r\n/**\r\n * Cookie管理\r\n * @param string|array  $name cookie名称，如果为数组表示进行cookie设置\r\n * @param mixed         $value cookie值\r\n * @param mixed         $option 参数\r\n * @return mixed\r\n */\r\ncookie($name, $value = \'\', $option = null);\r\n\r\n/**\r\n * 缓存管理\r\n * @param mixed     $name 缓存名称，如果为数组表示进行缓存设置\r\n * @param mixed     $value 缓存值\r\n * @param mixed     $options 缓存参数\r\n * @param string    $tag 缓存标签\r\n * @return mixed\r\n */\r\ncache($name, $value = \'\', $options = null, $tag = null);\r\n\r\n/**\r\n * 记录日志信息\r\n * @param mixed     $log log信息 支持字符串和数组\r\n * @param string    $level 日志级别\r\n * @return void|array\r\n */\r\ntrace($log = \'[think]\', $level = \'log\');\r\n\r\n// 获取当前Request对象实例\r\nrequest();\r\n\r\n/**\r\n * 创建普通 Response 对象实例\r\n * @param mixed      $data   输出数据\r\n * @param int|string $code   状态码\r\n * @param array      $header 头信息\r\n * @param string     $type\r\n * @return Response\r\n */\r\nresponse($data = [], $code = 200, $header = [], $type = \'html\');\r\n\r\n/**\r\n * 渲染模板输出\r\n * @param string    $template 模板文件\r\n * @param array     $vars 模板变量\r\n * @param array     $replace 模板替换\r\n * @param integer   $code 状态码\r\n * @return \\think\\response\\View\r\n */\r\nview($template = \'\', $vars = [], $replace = [], $code = 200);\r\n\r\n/**\r\n * 获取\\think\\response\\Json对象实例\r\n * @param mixed   $data 返回的数据\r\n * @param integer $code 状态码\r\n * @param array   $header 头部\r\n * @param array   $options 参数\r\n * @return \\think\\response\\Json\r\n */\r\njson($data = [], $code = 200, $header = [], $options = []);\r\n\r\n/**\r\n * 获取\\think\\response\\Jsonp对象实例\r\n * @param mixed   $data    返回的数据\r\n * @param integer $code    状态码\r\n * @param array   $header 头部\r\n * @param array   $options 参数\r\n * @return \\think\\response\\Jsonp\r\n */\r\njsonp($data = [], $code = 200, $header = [], $options = []);\r\n\r\n/**\r\n * 获取\\think\\response\\Xml对象实例\r\n * @param mixed   $data    返回的数据\r\n * @param integer $code    状态码\r\n * @param array   $header  头部\r\n * @param array   $options 参数\r\n * @return \\think\\response\\Xml\r\n */\r\nxml($data = [], $code = 200, $header = [], $options = []);\r\n\r\n/**\r\n * 获取\\think\\response\\Redirect对象实例\r\n * @param mixed         $url 重定向地址 支持Url::build方法的地址\r\n * @param array|integer $params 额外参数\r\n * @param integer       $code 状态码\r\n * @param array         $with 隐式传参\r\n * @return \\think\\response\\Redirect\r\n */\r\nredirect($url = [], $params = [], $code = 302, $with = []);\r\n\r\n/**\r\n * 抛出HTTP异常\r\n * @param integer|Response      $code 状态码 或者 Response对象实例\r\n * @param string                $message 错误信息\r\n * @param array                 $header 参数\r\n */\r\nabort($code, $message = null, $header = []);\r\n\r\n// 调试变量并且中断输出\r\nhalt($var);\r\n\r\n/**\r\n * 生成表单令牌\r\n * @param string $name 令牌名称\r\n * @param mixed  $type 令牌生成方法\r\n * @return string\r\n */\r\ntoken($name = \'__token__\', $type = \'md5\');\r\n\r\n/**\r\n * 延迟预载入关联查询\r\n * @param mixed $resultSet 数据集\r\n * @param mixed $relation 关联\r\n * @return array\r\n */\r\nload_relation($resultSet, $relation);\r\n\r\n// 数组转换为数据集对象\r\ncollection($resultSet);', '2017-04-25 08:20:09', '2017-04-30 21:17:58');
INSERT INTO `onethink_plugin_cheat_sheet_detail` VALUES (15, 1, 'http://www.kancloud.cn/manual/thinkphp5/118132', 'Lang', '/**\r\n * 设置语言定义(不区分大小写)\r\n * @param string|array  $name 语言变量\r\n * @param string        $value 语言值\r\n * @param string        $range 语言作用域\r\n * @return mixed\r\n */\r\nLang::set($name, $value = null, $range = \'\');\r\n\r\n/**\r\n * 加载语言定义(不区分大小写)\r\n * @param string $file 语言文件\r\n * @param string $range 语言作用域\r\n * @return mixed\r\n *\r\n Lang::load($file, $range = \'\');\r\n\r\n /**\r\n * 获取语言定义(不区分大小写)\r\n * @param string|null   $name 语言变量\r\n * @param array         $vars 变量替换\r\n * @param string        $range 语言作用域\r\n * @return mixed\r\n */\r\nLang::has($name, $range = \'\');\r\n\r\n/**\r\n * 获取语言定义(不区分大小写)\r\n * @param string|null   $name 语言变量\r\n * @param array         $vars 变量替换\r\n * @param string        $range 语言作用域\r\n * @return mixed\r\n */\r\nLang::get($name = null, $vars = [], $range = \'\');\r\n\r\n// 自动侦测设置获取语言选择\r\nLang::detect();\r\n\r\n// 设置语言自动侦测的变量\r\nLang::setLangDetectVar($var);\r\n\r\n// 设置语言的cookie保存变量\r\nLang::setLangCookieVar($var);\r\n\r\n// 设置语言的cookie的过期时间\r\nLang::setLangCookieExpire($expire);\r\n\r\n// 设置允许的语言列表\r\nLang::setAllowLangList($list);', '2017-04-25 08:24:35', '2017-04-30 21:18:21');
INSERT INTO `onethink_plugin_cheat_sheet_detail` VALUES (16, 1, 'http://www.kancloud.cn/manual/thinkphp5/118015', 'Loader', '// 自动加载\r\nLoader::autoload($class);\r\n\r\n// 查找文件\r\nLoader::findFile($class);\r\n\r\n// 注册classmap\r\nLoader::addClassMap($class, $map = \'\');\r\n\r\n// 注册命名空间\r\nLoader::addNamespace($namespace, $path = \'\');\r\n\r\n// 添加Ps0空间\r\nLoader::addPsr0($prefix, $paths, $prepend = false);\r\n\r\n// 添加Psr4空间\r\nLoader::addPsr4($prefix, $paths, $prepend = false);\r\n\r\n// 注册命名空间别名\r\nLoader::addNamespaceAlias($namespace, $original = \'\');\r\n\r\n// 注册自动加载机制\r\nLoader::register($autoload = \'\');\r\n\r\n// 注册composer自动加载\r\nLoader::registerComposerLoader();\r\n\r\n/**\r\n * 导入所需的类库 同java的Import 本函数有缓存功能\r\n * @param string $class   类库命名空间字符串\r\n * @param string $baseUrl 起始路径\r\n * @param string $ext     导入的文件扩展名\r\n * @return boolean\r\n */\r\nLoader::import($class, $baseUrl = \'\', $ext = EXT);\r\n\r\n/**\r\n * 实例化（分层）模型\r\n * @param string $name         Model名称\r\n * @param string $layer        业务层名称\r\n * @param bool   $appendSuffix 是否添加类名后缀\r\n * @param string $common       公共模块名\r\n * @return Object\r\n * @throws ClassNotFoundException\r\n */\r\nLoader::model($name = \'\', $layer = \'model\', $appendSuffix = false, $common = \'common\');\r\n\r\n/**\r\n * 实例化（分层）控制器 格式：[模块名/]控制器名\r\n * @param string $name         资源地址\r\n * @param string $layer        控制层名称\r\n * @param bool   $appendSuffix 是否添加类名后缀\r\n * @param string $empty        空控制器名称\r\n * @return Object|false\r\n * @throws ClassNotFoundException\r\n */\r\nLoader::controller($name, $layer = \'controller\', $appendSuffix = false, $empty = \'\');\r\n\r\n/**\r\n * 实例化验证类 格式：[模块名/]验证器名\r\n * @param string $name         资源地址\r\n * @param string $layer        验证层名称\r\n * @param bool   $appendSuffix 是否添加类名后缀\r\n * @param string $common       公共模块名\r\n * @return Object|false\r\n * @throws ClassNotFoundException\r\n */\r\nLoader::validate($name = \'\', $layer = \'validate\', $appendSuffix = false, $common = \'common\');\r\n\r\n/**\r\n * 数据库初始化 并取得数据库类实例\r\n * @param mixed         $config 数据库配置\r\n * @param bool|string   $name 连接标识 true 强制重新连接\r\n * @return \\think\\db\\Connection\r\n */\r\nLoader::db($config = [], $name = false);\r\n\r\n/**\r\n * 远程调用模块的操作方法 参数格式 [模块/控制器/]操作\r\n * @param string       $url          调用地址\r\n * @param string|array $vars         调用参数 支持字符串和数组\r\n * @param string       $layer        要调用的控制层名称\r\n * @param bool         $appendSuffix 是否添加类名后缀\r\n * @return mixed\r\n */\r\nLoader::action($url, $vars = [], $layer = \'controller\', $appendSuffix = false);\r\n\r\n/**\r\n * 字符串命名风格转换\r\n * type 0 将Java风格转换为C的风格 1 将C风格转换为Java的风格\r\n * @param string  $name 字符串\r\n * @param integer $type 转换类型\r\n * @param bool    $ucfirst 首字母是否大写（驼峰规则）\r\n * @return string\r\n */\r\nLoader::parseName($name, $type = 0, $ucfirst = true);\r\n\r\n/**\r\n * 解析应用类的类名\r\n * @param string $module 模块名\r\n * @param string $layer  层名 controller model ...\r\n * @param string $name   类名\r\n * @param bool   $appendSuffix\r\n * @return string\r\n */\r\nLoader::parseClass($module, $layer, $name, $appendSuffix = false);\r\n\r\n/**\r\n * 初始化类的实例\r\n * @return void\r\n */\r\nLoader::clearInstance();\r\n\r\n/**\r\n * 作用范围隔离\r\n *\r\n * @param $file\r\n * @return mixed\r\n */\r\n__include_file();\r\n\r\n__require_file($file);', '2017-04-25 08:35:56', '2017-04-30 21:19:03');
INSERT INTO `onethink_plugin_cheat_sheet_detail` VALUES (17, 1, 'http://www.kancloud.cn/manual/thinkphp5/118123', 'Log', '// 日志初始化\r\nLog::init($config = []);\r\n\r\n// 获取日志信息\r\nLog::getLog($type = \'\');\r\n\r\n/**\r\n * 记录调试信息\r\n * @param mixed  $msg  调试信息\r\n * @param string $type 信息类型\r\n * @return void\r\n */\r\n Log::record($msg, $type = \'log\');\r\n\r\n// 清空日志信息\r\nLog::clear();\r\n\r\n// 当前日志记录的授权key\r\nLog::key($key);\r\n\r\n/**\r\n * 检查日志写入权限\r\n * @param array  $config  当前日志配置参数\r\n * @return bool\r\n */\r\nLog::check($config);\r\n\r\n// 保存调试信息\r\nLog::save();\r\n\r\n/**\r\n * 实时写入日志信息 并支持行为\r\n * @param mixed  $msg  调试信息\r\n * @param string $type 信息类型\r\n * @param bool   $force 是否强制写入\r\n * @return bool\r\n */\r\nLog::write($msg, $type = \'log\', $force = false);\r\n\r\n// 静态调用\r\nLog::__callStatic($method, $args);', '2017-04-25 08:42:22', '2017-04-30 21:19:47');
INSERT INTO `onethink_plugin_cheat_sheet_detail` VALUES (18, 1, 'http://www.kancloud.cn/manual/thinkphp5/135186', 'Model', '$model = new Model();\r\n\r\n/**\r\n * 获取当前模型的数据库查询对象\r\n * @access public\r\n * @param bool $baseQuery 是否调用全局查询范围\r\n * @return Query\r\n */\r\n$model->db($baseQuery = true);\r\n\r\n$this->initialize();\r\n\r\n/**\r\n * 设置数据对象值\r\n * @access public\r\n * @param mixed $data  数据或者属性名\r\n * @param mixed $value 值\r\n * @return $this\r\n */\r\n$model->data($data, $value = null);\r\n\r\n/**\r\n * 获取对象原始数据 如果不存在指定字段返回false\r\n * @access public\r\n * @param string $name 字段名 留空获取全部\r\n * @return mixed\r\n * @throws InvalidArgumentException\r\n */\r\n $model->getData($name = null);\r\n\r\n /**\r\n * 修改器 设置数据对象值\r\n * @access public\r\n * @param string $name  属性名\r\n * @param mixed  $value 属性值\r\n * @param array  $data  数据\r\n * @return $this\r\n */\r\n$model->setAttr($name, $value, $data = []);\r\n\r\n/**\r\n * 自动写入时间戳\r\n * @access public\r\n * @param string $name 时间戳字段\r\n * @return mixed\r\n */\r\n$this->autoWriteTimestamp($name);\r\n\r\n/**\r\n * 时间日期字段格式化处理\r\n * @access public\r\n * @param mixed $time      时间日期表达式\r\n * @param mixed $format    日期格式\r\n * @param bool  $timestamp 是否进行时间戳转换\r\n * @return mixed\r\n */\r\n$this->formatDateTime($time, $format, $timestamp = false);\r\n\r\n/**\r\n * 数据写入 类型转换\r\n * @access public\r\n * @param mixed        $value 值\r\n * @param string|array $type  要转换的类型\r\n * @return mixed\r\n */\r\n$this->writeTransform($value, $type);\r\n\r\n// 获取器 获取数据对象的值\r\n$model->getAttr($name);\r\n\r\n/**\r\n * 数据读取 类型转换\r\n * @access public\r\n * @param mixed        $value 值\r\n * @param string|array $type  要转换的类型\r\n * @return mixed\r\n */\r\n$this->readTransform($value, $type);\r\n\r\n/**\r\n * 设置需要追加的输出属性\r\n * @access public\r\n * @param array $append   属性列表\r\n * @param bool  $override 是否覆盖\r\n * @return $this\r\n */\r\n$model->append($append = [], $override = false);\r\n\r\n/**\r\n * 设置附加关联对象的属性\r\n * @access public\r\n * @param string       $relation 关联方法\r\n * @param string|array $append   追加属性名\r\n * @return $this\r\n * @throws Exception\r\n */\r\n$model->appendRelationAttr($relation, $append);\r\n\r\n/**\r\n * 设置需要隐藏的输出属性\r\n * @access public\r\n * @param array $hidden   属性列表\r\n * @param bool  $override 是否覆盖\r\n * @return $this\r\n */\r\n$model->hidden($hidden = [], $override = false);\r\n\r\n/**\r\n * 设置需要输出的属性\r\n * @access public\r\n * @param array $visible\r\n * @param bool  $override 是否覆盖\r\n * @return $this\r\n */\r\n$model->visible($visible = [], $override = false);\r\n\r\n/**\r\n * 解析隐藏及显示属性\r\n * @access protected\r\n * @param array $attrs  属性\r\n * @param array $result 结果集\r\n * @param bool  $visible\r\n * @return array\r\n */\r\n$this->parseAttr($attrs, &$result, $visible = true);\r\n\r\n/**\r\n * 转换子模型对象\r\n * @access protected\r\n * @param Model|ModelCollection $model\r\n * @param                  $visible\r\n * @param                  $hidden\r\n * @param                  $key\r\n * @return array\r\n */\r\n$this->subToArray($model, $visible, $hidden, $key);\r\n\r\n// 转换当前模型对象为数组\r\n$model->toArray();\r\n\r\n/**\r\n * 转换当前模型对象为JSON字符串\r\n * @access public\r\n * @param integer $options json参数\r\n * @return string\r\n */\r\n$model->toJson($options = JSON_UNESCAPED_UNICODE);\r\n\r\n/**\r\n * 转换当前模型数据集为数据集对象\r\n * @access public\r\n * @param array|\\think\\Collection $collection 数据集\r\n * @return \\think\\Collection\r\n */\r\n$model->toCollection($collection);\r\n\r\n/**\r\n * 关联数据一起更新\r\n * @access public\r\n * @param mixed $relation 关联\r\n * @return $this\r\n */\r\n$model->together($relation);\r\n\r\n/**\r\n * 获取模型对象的主键\r\n * @access public\r\n * @param string $name 模型名\r\n * @return mixed\r\n */\r\n$model->getPk($name = \'\');\r\n\r\n// 判断一个字段名是否为主键字段\r\n$this->isPk($key);\r\n\r\n/**\r\n * 保存当前数据对象\r\n * @access public\r\n * @param array  $data     数据\r\n * @param array  $where    更新条件\r\n * @param string $sequence 自增序列名\r\n * @return integer|false\r\n */\r\n$model->save($data = [], $where = [], $sequence = null);\r\n\r\n/**\r\n * 保存多个数据到当前数据对象\r\n * @access public\r\n * @param array   $dataSet 数据\r\n * @param boolean $replace 是否自动识别更新和写入\r\n * @return array|false\r\n * @throws \\Exception\r\n */\r\n$model->saveAll($dataSet, $replace = true);\r\n\r\n/**\r\n * 设置允许写入的字段\r\n * @access public\r\n * @param mixed $field 允许写入的字段 如果为true只允许写入数据表字段\r\n * @return $this\r\n */\r\n$model->allowField($field);\r\n\r\n/**\r\n * 设置只读字段\r\n * @access public\r\n * @param mixed $field 只读字段\r\n * @return $this\r\n */\r\n$model->readonly($field);\r\n\r\n/**\r\n * 是否为更新数据\r\n * @access public\r\n * @param bool  $update\r\n * @param mixed $where\r\n * @return $this\r\n */\r\n$model->isUpdate($update = true, $where = null);\r\n\r\n/**\r\n * 数据自动完成\r\n * @access public\r\n * @param array $auto 要自动更新的字段列表\r\n * @return void\r\n */\r\n$this->autoCompleteData($auto = []);\r\n\r\n// 删除当前的记录\r\n$model->delete();\r\n\r\n/**\r\n * 设置自动完成的字段（ 规则通过修改器定义）\r\n * @access public\r\n * @param array $fields 需要自动完成的字段\r\n * @return $this\r\n */\r\n$model->auto($fields);\r\n\r\n/**\r\n * 设置字段验证\r\n * @access public\r\n * @param array|string|bool $rule  验证规则 true表示自动读取验证器类\r\n * @param array             $msg   提示信息\r\n * @param bool              $batch 批量验证\r\n * @return $this\r\n */\r\n$model->validate($rule = true, $msg = [], $batch = false);\r\n\r\n/**\r\n * 设置验证失败后是否抛出异常\r\n * @access public\r\n * @param bool $fail 是否抛出异常\r\n * @return $this\r\n */\r\n$model->validateFailException($fail = true);\r\n\r\n/**\r\n * 自动验证数据\r\n * @access protected\r\n * @param array $data  验证数据\r\n * @param mixed $rule  验证规则\r\n * @param bool  $batch 批量验证\r\n * @return bool\r\n */\r\n$this->validateData($data, $rule = null, $batch = null);\r\n\r\n// 返回模型的错误信息\r\n$this->getError();\r\n\r\n/**\r\n * 注册回调方法\r\n * @access public\r\n * @param string   $event    事件名\r\n * @param callable $callback 回调方法\r\n * @param bool     $override 是否覆盖\r\n * @return void\r\n */\r\nModel::event($event, $callback, $override = false);\r\n\r\n/**\r\n * 触发事件\r\n * @access protected\r\n * @param string $event  事件名\r\n * @param mixed  $params 传入参数（引用）\r\n * @return bool\r\n */\r\n $this->trigger($event, &$params);\r\n\r\n /**\r\n * 写入数据\r\n * @access public\r\n * @param array      $data  数据数组\r\n * @param array|true $field 允许字段\r\n * @return $this\r\n */\r\nModel::create($data = [], $field = null);\r\n\r\n/**\r\n * 更新数据\r\n * @access public\r\n * @param array      $data  数据数组\r\n * @param array      $where 更新条件\r\n * @param array|true $field 允许字段\r\n * @return $this\r\n */\r\nModel::update($data = [], $where = [], $field = null);\r\n\r\n/**\r\n * 查找单条记录\r\n * @access public\r\n * @param mixed        $data  主键值或者查询条件（闭包）\r\n * @param array|string $with  关联预查询\r\n * @param bool         $cache 是否缓存\r\n * @return static\r\n * @throws exception\\DbException\r\n */\r\nModel::get($data = null, $with = [], $cache = false);\r\n\r\n/**\r\n * 查找所有记录\r\n * @access public\r\n * @param mixed        $data  主键列表或者查询条件（闭包）\r\n * @param array|string $with  关联预查询\r\n * @param bool         $cache 是否缓存\r\n * @return static[]|false\r\n * @throws exception\\DbException\r\n */\r\nModel::all($data = null, $with = [], $cache = false);\r\n\r\n/**\r\n * 分析查询表达式\r\n * @access public\r\n * @param mixed  $data  主键列表或者查询条件（闭包）\r\n * @param string $with  关联预查询\r\n * @param bool   $cache 是否缓存\r\n * @return Query\r\n */\r\nModel::parseQuery(&$data, $with, $cache);\r\n\r\n/**\r\n * 删除记录\r\n * @access public\r\n * @param mixed $data 主键列表 支持闭包查询条件\r\n * @return integer 成功删除的记录数\r\n */\r\nModel::destroy($data);\r\n\r\n/**\r\n * 命名范围\r\n * @access public\r\n * @param string|array|\\Closure $name 命名范围名称 逗号分隔\r\n * @internal  mixed                 ...$params 参数调用\r\n * @return Model|Query\r\n */\r\nModel::scope($name);\r\n\r\n// 设置是否使用全局查询范围\r\nModel::useGlobalScope($use);\r\n\r\n/**\r\n * 根据关联条件查询当前模型\r\n * @access public\r\n * @param string  $relation 关联方法名\r\n * @param mixed   $operator 比较操作符\r\n * @param integer $count    个数\r\n * @param string  $id       关联表的统计字段\r\n * @return Relation|Query\r\n */\r\nModel::has($relation, $operator = \'>=\', $count = 1, $id = \'*\');\r\n\r\n/**\r\n * 根据关联条件查询当前模型\r\n * @access public\r\n * @param string $relation 关联方法名\r\n * @param mixed  $where    查询条件（数组或者闭包）\r\n * @return Relation|Query\r\n */\r\nModel::hasWhere($relation, $where = []);\r\n\r\n/**\r\n * 解析模型的完整命名空间\r\n * @access public\r\n * @param string $model 模型名（或者完整类名）\r\n * @return string\r\n */\r\n $this->parseModel($model);\r\n\r\n /**\r\n * 查询当前模型的关联数据\r\n * @access public\r\n * @param string|array $relations 关联名\r\n * @return $this\r\n */\r\n$model->relationQuery($relations);\r\n\r\n/**\r\n * 预载入关联查询 返回数据集\r\n * @access public\r\n * @param array  $resultSet 数据集\r\n * @param string $relation  关联名\r\n * @return array\r\n */\r\n$model->eagerlyResultSet(&$resultSet, $relation);\r\n\r\n/**\r\n * 预载入关联查询 返回模型对象\r\n * @access public\r\n * @param Model  $result   数据对象\r\n * @param string $relation 关联名\r\n * @return Model\r\n */\r\n$model->eagerlyResult(&$result, $relation);\r\n\r\n/**\r\n * 关联统计\r\n * @access public\r\n * @param Model        $result   数据对象\r\n * @param string|array $relation 关联名\r\n * @return void\r\n */\r\n$model->relationCount(&$result, $relation);\r\n\r\n/**\r\n * 获取模型的默认外键名\r\n * @access public\r\n * @param string $name 模型名\r\n * @return string\r\n */\r\n$this->getForeignKey($name);\r\n\r\n/**\r\n * HAS ONE 关联定义\r\n * @access public\r\n * @param string $model      模型名\r\n * @param string $foreignKey 关联外键\r\n * @param string $localKey   关联主键\r\n * @param array  $alias      别名定义（已经废弃）\r\n * @param string $joinType   JOIN类型\r\n * @return HasOne\r\n */\r\n$model->hasOne($model, $foreignKey = \'\', $localKey = \'\', $alias = [], $joinType = \'INNER\');\r\n\r\n/**\r\n * BELONGS TO 关联定义\r\n * @access public\r\n * @param string $model      模型名\r\n * @param string $foreignKey 关联外键\r\n * @param string $localKey   关联主键\r\n * @param array  $alias      别名定义（已经废弃）\r\n * @param string $joinType   JOIN类型\r\n * @return BelongsTo\r\n */\r\n$model->belongsTo($model, $foreignKey = \'\', $localKey = \'\', $alias = [], $joinType = \'INNER\');\r\n\r\n/**\r\n * HAS MANY 关联定义\r\n * @access public\r\n * @param string $model      模型名\r\n * @param string $foreignKey 关联外键\r\n * @param string $localKey   关联主键\r\n * @return HasMany\r\n */\r\n$model->hasMany($model, $foreignKey = \'\', $localKey = \'\');\r\n\r\n/**\r\n * HAS MANY 远程关联定义\r\n * @access public\r\n * @param string $model      模型名\r\n * @param string $through    中间模型名\r\n * @param string $foreignKey 关联外键\r\n * @param string $throughKey 关联外键\r\n * @param string $localKey   关联主键\r\n * @return HasManyThrough\r\n */\r\n$model->hasManyThrough($model, $through, $foreignKey = \'\', $throughKey = \'\', $localKey = \'\');\r\n\r\n/**\r\n * BELONGS TO MANY 关联定义\r\n * @access public\r\n * @param string $model      模型名\r\n * @param string $table      中间表名\r\n * @param string $foreignKey 关联外键\r\n * @param string $localKey   当前模型关联键\r\n * @return BelongsToMany\r\n */\r\n$model->belongsToMany($model, $table = \'\', $foreignKey = \'\', $localKey = \'\');\r\n\r\n/**\r\n * MORPH  MANY 关联定义\r\n * @access public\r\n * @param string       $model 模型名\r\n * @param string|array $morph 多态字段信息\r\n * @param string       $type  多态类型\r\n * @return MorphMany\r\n */\r\n$model->morphMany($model, $morph = null, $type = \'\');\r\n\r\n/**\r\n * MORPH TO 关联定义\r\n * @access public\r\n * @param string|array $morph 多态字段信息\r\n * @param array        $alias 多态别名定义\r\n * @return MorphTo\r\n */\r\n$model->morphTo($morph = null, $alias = []);\r\n\r\n$model->__call($method, $args);\r\n\r\nModel::__callStatic($method, $params);\r\n\r\n/**\r\n * 修改器 设置数据对象的值\r\n * @access public\r\n * @param string $name  名称\r\n * @param mixed  $value 值\r\n * @return void\r\n */\r\n $model->__set($name, $value);\r\n\r\n/**\r\n * 获取器 获取数据对象的值\r\n * @access public\r\n * @param string $name 名称\r\n * @return mixed\r\n */\r\n$model->__get($name);\r\n\r\n// 检测数据对象的值\r\n$model->__isset($name);\r\n\r\n/**\r\n * 销毁数据对象的值\r\n * @access public\r\n * @param string $name 名称\r\n * @return void\r\n */\r\n$model->__unset($name);\r\n\r\n$model->__toString();\r\n\r\n$model->jsonSerialize();\r\n\r\n$model->offsetSet($name, $value);\r\n\r\n$model->offsetExists($name);\r\n\r\n$model->offsetUnset($name);\r\n\r\n$model->offsetGet($name);\r\n\r\n// 解序列化后处理\r\n$model->__wakeup();\r\n\r\n// 模型事件快捷方法\r\nModel::beforeInsert($callback, $override = false);\r\n\r\nModel::afterInsert($callback, $override = false);\r\n\r\nModel::beforeUpdate($callback, $override = false);\r\n\r\nModel::afterUpdate($callback, $override = false);\r\n\r\nModel::beforeWrite($callback, $override = false);\r\n\r\nModel::afterWrite($callback, $override = false);\r\n\r\nModel::beforeDelete($callback, $override = false);\r\n\r\nModel::afterDelete($callback, $override = false);', '2017-04-25 09:43:48', '2017-04-30 21:20:22');
INSERT INTO `onethink_plugin_cheat_sheet_detail` VALUES (19, 1, '', 'Process', '/**\r\n * 构造方法\r\n * @param string         $commandline 指令\r\n * @param string|null    $cwd         工作目录\r\n * @param array|null     $env         环境变量\r\n * @param string|null    $input       输入\r\n * @param int|float|null $timeout     超时时间\r\n * @param array          $options     proc_open的选项\r\n * @throws \\RuntimeException\r\n * @api\r\n */\r\n$process = new Process($commandline, $cwd = null, array $env = null, $input = null, $timeout = 60, array $options = []);\r\n\r\n// 停止\r\n$process->__destruct();\r\n\r\n// 克隆\r\n$process->__clone();\r\n\r\n/**\r\n * 运行指令\r\n * @param callback|null $callback\r\n * @return int\r\n */\r\n$process->run($callback = null);\r\n\r\n/**\r\n * 运行指令\r\n * @param callable|null $callback\r\n * @return self\r\n * @throws \\RuntimeException\r\n * @throws ProcessFailedException\r\n */\r\n$process->mustRun($callback = null);\r\n\r\n/**\r\n * 启动进程并写到 STDIN 输入后返回。\r\n * @param callable|null $callback\r\n * @throws \\RuntimeException\r\n * @throws \\RuntimeException\r\n * @throws \\LogicException\r\n */\r\n $process->start($callback = null);\r\n\r\n /**\r\n * 重启进程\r\n * @param callable|null $callback\r\n * @return Process\r\n * @throws \\RuntimeException\r\n * @throws \\RuntimeException\r\n */\r\n$process->restart($callback = null);\r\n\r\n/**\r\n * 等待要终止的进程\r\n * @param callable|null $callback\r\n * @return int\r\n */\r\n$process->wait($callback = null);\r\n\r\n/**\r\n * 获取PID\r\n * @return int|null\r\n * @throws \\RuntimeException\r\n */\r\n$process->getPid();\r\n\r\n/**\r\n * 将一个 POSIX 信号发送到进程中\r\n * @param int $signal\r\n * @return Process\r\n */\r\n$process->signal($signal);\r\n\r\n// 禁用从底层过程获取输出和错误输出。\r\n$process->disableOutput();\r\n\r\n// 开启从底层过程获取输出和错误输出。\r\n$process->enableOutput();\r\n\r\n// 输出是否禁用\r\n$process->isOutputDisabled();\r\n\r\n/**\r\n * 获取当前的输出管道\r\n * @return string\r\n * @throws \\LogicException\r\n * @throws \\LogicException\r\n * @api\r\n */\r\n$process->getOutput();\r\n\r\n// 以增量方式返回的输出结果。\r\n$process->getIncrementalOutput();\r\n\r\n// 清空输出\r\n$process->clearOutput();\r\n\r\n// 返回当前的错误输出的过程 (STDERR)。\r\n$process->getErrorOutput();\r\n\r\n// 以增量方式返回 errorOutput\r\n$process->getIncrementalErrorOutput();\r\n\r\n// 清空 errorOutput\r\n$process->clearErrorOutput();\r\n\r\n/**\r\n * 获取退出码\r\n * @return null|int\r\n */\r\n$process->getExitCode();\r\n\r\n/**\r\n * 获取退出文本\r\n * @return null|string\r\n */\r\n$process->getExitCodeText();\r\n\r\n// 检查是否成功\r\n$process->isSuccessful();\r\n\r\n// 是否未捕获的信号已被终止子进程\r\n$process->hasBeenSignaled();\r\n\r\n// 返回导致子进程终止其执行的数。\r\n$process->getTermSignal();\r\n\r\n// 检查是否正在运行\r\n$process->isRunning();\r\n\r\n// 检查是否已开始\r\n$process->isStarted();\r\n\r\n// 检查是否已终止\r\n$process->isTerminated();\r\n\r\n// 获取当前的状态\r\n$process->getStatus();\r\n\r\n// 终止进程\r\n$process->stop();\r\n\r\n// 添加一行输出\r\n$process->addOutput($line);\r\n\r\n// 添加一行错误输出\r\n$process->addErrorOutput($line);\r\n\r\n// 获取被执行的指令\r\n$process->getCommandLine();\r\n\r\n// 设置指令\r\n$process->setCommandLine($commandline);\r\n\r\n// 获取超时时间\r\n$process->getTimeout();\r\n\r\n// 获取idle超时时间\r\n$process->getIdleTimeout();\r\n\r\n// 设置超时时间\r\n$process->setTimeout($timeout);\r\n\r\n// 设置idle超时时间\r\n$process->setIdleTimeout($timeout);\r\n\r\n// 设置TTY\r\n$process->setTty($tty);\r\n\r\n// 检查是否是tty模式\r\n$process->isTty();\r\n\r\n// 设置pty模式\r\n$process->setPty($bool);\r\n\r\n// 是否是pty模式\r\n$process->isPty();\r\n\r\n// 获取工作目录\r\n$process->getWorkingDirectory();\r\n\r\n// 设置工作目录\r\n$process->setWorkingDirectory($cwd);\r\n\r\n// 获取环境变量\r\n$process->getEnv();\r\n\r\n// 设置环境变量\r\n$process->setEnv(array $env);\r\n\r\n// 获取输入\r\n$process->getInput();\r\n\r\n// 设置输入\r\n$process->setInput($input);\r\n\r\n// 获取proc_open的选项\r\n$process->getOptions();\r\n\r\n// 设置proc_open的选项\r\n$process->setOptions(array $options);\r\n\r\n// 是否兼容windows\r\n$process->getEnhanceWindowsCompatibility();\r\n\r\n// 设置是否兼容windows\r\n$process->setEnhanceWindowsCompatibility($enhance);\r\n\r\n// 返回是否 sigchild 兼容模式激活\r\n$process->getEnhanceSigchildCompatibility();\r\n\r\n// 激活 sigchild 兼容性模式。\r\n$process->setEnhanceSigchildCompatibility($enhance);\r\n\r\n// 是否超时\r\n$process->checkTimeout();\r\n\r\n// 是否支持pty\r\nProcess::isPtySupported();\r\n\r\n// 创建所需的 proc_open 的描述符\r\n$this->getDescriptors();\r\n\r\n// 建立 wait () 使用的回调。\r\n$this->buildCallback($callback);\r\n\r\n// 更新状态\r\n$this->updateStatus($blocking);\r\n\r\n// 是否开启 \'--enable-sigchild\'\r\n$this->isSigchildEnabled();\r\n\r\n// 验证是否超时\r\n$this->validateTimeout($timeout);\r\n\r\n// 读取pipes\r\n$this->readPipes($blocking, $close);\r\n\r\n// 捕获退出码\r\n$this->captureExitCode();\r\n\r\n/**\r\n * 关闭资源\r\n * @return int 退出码\r\n */\r\n$this->close();\r\n\r\n// 重置数据\r\n$this->resetProcessData();\r\n\r\n// 将一个 POSIX 信号发送到进程中。\r\n$this->doSignal($signal, $throwException);\r\n\r\n// 确保进程已经开启\r\n$this->requireProcessIsStarted($functionName);\r\n\r\n// 确保进程已经终止\r\n$this->requireProcessIsTerminated($functionName);', '2017-04-27 08:29:56', '2017-04-27 08:29:56');
INSERT INTO `onethink_plugin_cheat_sheet_detail` VALUES (20, 1, 'http://www.kancloud.cn/manual/thinkphp5/118042', 'Request', '/**\r\n * Hook 方法注入\r\n * @access public\r\n * @param string|array  $method 方法名\r\n * @param mixed         $callback callable\r\n * @return void\r\n */\r\nRequest::hook($method, $callback = null);\r\n\r\n// 初始化\r\nRequest::instance($options = []);\r\n\r\n/**\r\n * 创建一个URL请求\r\n * @access public\r\n * @param string    $uri URL地址\r\n * @param string    $method 请求类型\r\n * @param array     $params 请求参数\r\n * @param array     $cookie\r\n * @param array     $files\r\n * @param array     $server\r\n * @param string    $content\r\n * @return \\think\\Request\r\n */\r\nRequest::create($uri, $method = \'GET\', $params = [], $cookie = [], $files = [], $server = [], $content = null);\r\n\r\n// 设置或获取当前包含协议的域名\r\n$request = new Request();\r\n$request->domain($domain = null);\r\n\r\n/**\r\n * 设置或获取当前完整URL 包括QUERY_STRING\r\n * @access public\r\n * @param string|true $url URL地址 true 带域名获取\r\n * @return string\r\n */\r\n\r\n/**\r\n * 设置或获取当前URL 不含QUERY_STRING\r\n * @access public\r\n * @param string $url URL地址\r\n * @return string\r\n */\r\n$request->baseUrl($url = null);\r\n\r\n/**\r\n * 设置或获取当前执行的文件 SCRIPT_NAME\r\n * @access public\r\n * @param string $file 当前执行的文件\r\n * @return string\r\n */\r\n$request->baseFile($file = null);\r\n\r\n/**\r\n * 设置或获取URL访问根地址\r\n * @access public\r\n * @param string $url URL地址\r\n * @return string\r\n */\r\n$request->root($url = null);\r\n\r\n// 获取当前请求URL的pathinfo信息（含URL后缀）\r\n$request->pathinfo();\r\n\r\n// 获取当前请求URL的pathinfo信息(不含URL后缀)\r\n$request->path();\r\n\r\n// 当前URL的访问后缀\r\n$request->ext();\r\n\r\n// 获取当前请求的时间\r\n$request->time($float = false);\r\n\r\n/**\r\n * 当前请求的资源类型\r\n * @access public\r\n * @return false|string\r\n */\r\n$request->type();\r\n\r\n/**\r\n * 设置资源类型\r\n * @access public\r\n * @param string|array  $type 资源类型名\r\n * @param string        $val 资源类型\r\n * @return void\r\n */\r\n$request->mimeType($type, $val = \'\');\r\n\r\n/**\r\n * 当前的请求类型\r\n * @access public\r\n * @param bool $method  true 获取原始请求类型\r\n * @return string\r\n */\r\n$request->method($method = false);\r\n\r\n// 是否为GET请求\r\n$request->isGet();\r\n\r\n// 是否为POST请求\r\n$request->isPost();\r\n\r\n// 是否为PUT请求\r\n$request->isPut();\r\n\r\n// 是否为DELTE请求\r\n$request->isDelete();\r\n\r\n// 是否为HEAD请求\r\n$request->isHead();\r\n\r\n// 是否为PATCH请求\r\n$request->isPatch();\r\n\r\n// 是否为OPTIONS请求\r\n$request->isOptions();\r\n\r\n// 是否为cli\r\n$request->isCli();\r\n\r\n// 是否为cgi\r\n$request->isCgi();\r\n\r\n/**\r\n * 获取获取当前请求的参数\r\n * @access public\r\n * @param string|array  $name 变量名\r\n * @param mixed         $default 默认值\r\n * @param string|array  $filter 过滤方法\r\n * @return mixed\r\n */\r\n$request->param($name = \'\', $default = null, $filter = \'\');\r\n\r\n/**\r\n * 设置获取获取路由参数\r\n * @access public\r\n * @param string|array  $name 变量名\r\n * @param mixed         $default 默认值\r\n * @param string|array  $filter 过滤方法\r\n * @return mixed\r\n */\r\n$request->route($name = \'\', $default = null, $filter = \'\');\r\n\r\n/**\r\n * 设置获取获取GET参数\r\n * @access public\r\n * @param string|array  $name 变量名\r\n * @param mixed         $default 默认值\r\n * @param string|array  $filter 过滤方法\r\n * @return mixed\r\n */ \r\n$request->get($name = \'\', $default = null, $filter = \'\');\r\n\r\n/**\r\n * 设置获取获取POST参数\r\n * @access public\r\n * @param string        $name 变量名\r\n * @param mixed         $default 默认值\r\n * @param string|array  $filter 过滤方法\r\n * @return mixed\r\n */\r\n$request->post($name = \'\', $default = null, $filter = \'\');\r\n\r\n/**\r\n * 设置获取获取PUT参数\r\n * @access public\r\n * @param string|array      $name 变量名\r\n * @param mixed             $default 默认值\r\n * @param string|array      $filter 过滤方法\r\n * @return mixed\r\n */\r\n$request->put($name = \'\', $default = null, $filter = \'\');\r\n\r\n/**\r\n * 设置获取获取DELETE参数\r\n * @access public\r\n * @param string|array      $name 变量名\r\n * @param mixed             $default 默认值\r\n * @param string|array      $filter 过滤方法\r\n * @return mixed\r\n */\r\n$request->delete($name = \'\', $default = null, $filter = \'\');\r\n\r\n/**\r\n * 设置获取获取PATCH参数\r\n * @access public\r\n * @param string|array      $name 变量名\r\n * @param mixed             $default 默认值\r\n * @param string|array      $filter 过滤方法\r\n * @return mixed\r\n */\r\n$Req\r\n\r\n\r\n/**\r\n * 获取request变量\r\n * @param string        $name 数据名称\r\n * @param string        $default 默认值\r\n * @param string|array  $filter 过滤方法\r\n * @return mixed\r\n */\r\n$request->request($name = \'\', $default = null, $filter = \'\');\r\n\r\n/**\r\n * 获取session数据\r\n * @access public\r\n * @param string|array  $name 数据名称\r\n * @param string        $default 默认值\r\n * @param string|array  $filter 过滤方法\r\n * @return mixed\r\n */\r\n$request->session($name = \'\', $default = null, $filter = \'\');\r\n\r\n\r\n/**\r\n * 获取cookie参数\r\n * @access public\r\n * @param string|array  $name 数据名称\r\n * @param string        $default 默认值\r\n * @param string|array  $filter 过滤方法\r\n * @return mixed\r\n */\r\n$request->cookie($name = \'\', $default = null, $filter = \'\');\r\n\r\n/**\r\n * 获取server参数\r\n * @access public\r\n * @param string|array  $name 数据名称\r\n * @param string        $default 默认值\r\n * @param string|array  $filter 过滤方法\r\n * @return mixed\r\n */\r\n$request->server($name = \'\', $default = null, $filter = \'\');\r\n\r\n/**\r\n * 获取上传的文件信息\r\n * @access public\r\n * @param string|array $name 名称\r\n * @return null|array|\\think\\File\r\n */\r\n$request->file($name = \'\');\r\n\r\n/**\r\n * 获取环境变量\r\n * @param string|array  $name 数据名称\r\n * @param string        $default 默认值\r\n * @param string|array  $filter 过滤方法\r\n * @return mixed\r\n */\r\n$request->env($name = \'\', $default = null, $filter = \'\');\r\n\r\n/**\r\n * 设置或者获取当前的Header\r\n * @access public\r\n * @param string|array  $name header名称\r\n * @param string        $default 默认值\r\n * @return string\r\n */\r\n$request->header($name = \'\', $default = null);\r\n\r\n/**\r\n * 获取变量 支持过滤和默认值\r\n * @param array         $data 数据源\r\n * @param string|false  $name 字段名\r\n * @param mixed         $default 默认值\r\n * @param string|array  $filter 过滤函数\r\n * @return mixed\r\n */\r\n $request->input($data = [], $name = \'\', $default = null, $filter = \'\');\r\n\r\n /**\r\n * 设置或获取当前的过滤规则\r\n * @param mixed $filter 过滤规则\r\n * @return mixed\r\n */\r\n$request->filter($filter = null)；\r\n\r\n/**\r\n * 递归过滤给定的值\r\n * @param mixed     $value 键值\r\n * @param mixed     $key 键名\r\n * @param array     $filters 过滤方法+默认值\r\n * @return mixed\r\n */\r\n $this->filterValue(&$value, $key, $filters);\r\n\r\n // 过滤表单中的表达式\r\n $request->filterExp(&$value);\r\n\r\n /**\r\n * 强制类型转换\r\n * @param string $data\r\n * @param string $type\r\n * @return mixed\r\n */\r\n$this->typeCast(&$data, $type);\r\n\r\n/**\r\n * 是否存在某个请求参数\r\n * @access public\r\n * @param string    $name 变量名\r\n * @param string    $type 变量类型\r\n * @param bool      $checkEmpty 是否检测空值\r\n * @return mixed\r\n */\r\n$request->has($name, $type = \'param\', $checkEmpty = false);\r\n\r\n/**\r\n * 获取指定的参数\r\n * @access public\r\n * @param string|array  $name 变量名\r\n * @param string        $type 变量类型\r\n * @return mixed\r\n */\r\n$request->only($name, $type = \'param\');\r\n\r\n/**\r\n * 排除指定参数获取\r\n * @access public\r\n * @param string|array  $name 变量名\r\n * @param string        $type 变量类型\r\n * @return mixed\r\n */\r\n$request->except($name, $type = \'param\');\r\n\r\n// 当前是否ssl\r\n$request->isSsl();\r\n\r\n/**\r\n * 当前是否Ajax请求\r\n * @access public\r\n * @param bool $ajax  true 获取原始ajax请求\r\n * @return bool\r\n */\r\n$request->isAjax($ajax = false);\r\n\r\n/**\r\n * 当前是否Pjax请求\r\n * @access public\r\n * @param bool $pjax  true 获取原始pjax请求\r\n * @return bool\r\n */\r\n$request->isPjax($pjax = false);\r\n\r\n/**\r\n * 获取客户端IP地址\r\n * @param integer   $type 返回类型 0 返回IP地址 1 返回IPV4地址数字\r\n * @param boolean   $adv 是否进行高级模式获取（有可能被伪装）\r\n * @return mixed\r\n */\r\n$requet->ip($type = 0, $adv = false);\r\n\r\n// 检测是否使用手机访问\r\n$request->isMobile();\r\n\r\n// 当前URL地址中的scheme参数\r\n$request->scheme();\r\n\r\n// 当前请求URL地址中的query参数\r\n$request->query();\r\n\r\n// 当前请求的host\r\n$request->host();\r\n\r\n// 当前请求URL地址中的port参数\r\n$request->port();\r\n\r\n// 当前请求 SERVER_PROTOCOL\r\n$request->protocol();\r\n\r\n// 当前请求 REMOTE_PORT\r\n$request->remotePort();\r\n\r\n// 当前请求 HTTP_CONTENT_TYPE\r\n$request->contentType();\r\n\r\n// 获取当前请求的路由信息\r\n$request->routeInfo($route = []);\r\n\r\n/**\r\n * 设置或者获取当前请求的调度信息\r\n * @access public\r\n * @param array  $dispatch 调度信息\r\n * @return array\r\n */\r\n$request->dispatch($dispatch = null);\r\n\r\n/**\r\n * 设置或者获取当前的模块名\r\n * @access public\r\n * @param string $module 模块名\r\n * @return string|Request\r\n */\r\n$request->module($module = null);\r\n\r\n// 设置或者获取当前的控制器名\r\n$request->controller($controller = null);\r\n\r\n// 设置或者获取当前的操作名\r\n$request->action($action = null);\r\n\r\n// 设置或者获取当前的语言\r\n$request->langset($lang = null);\r\n\r\n// 设置或者获取当前请求的content\r\n$request->getContent();\r\n\r\n// 获取当前请求的php://input\r\n$request->getInput();\r\n\r\n/**\r\n * 生成请求令牌\r\n * @access public\r\n * @param string $name 令牌名称\r\n * @param mixed  $type 令牌生成方法\r\n * @return string\r\n */\r\n$request->token($name = \'__token__\', $type = \'md5\');\r\n\r\n/**\r\n * 设置当前地址的请求缓存\r\n * @access public\r\n * @param string $key 缓存标识，支持变量规则 ，例如 item/:name/:id\r\n * @param mixed  $expire 缓存有效期\r\n * @param array  $except 缓存排除\r\n * @return void\r\n */\r\n$request->cache($key, $expire = null, $except = []);\r\n\r\n// 读取请求缓存设置\r\n$request->getCache();\r\n\r\n/**\r\n * 设置当前请求绑定的对象实例\r\n * @access public\r\n * @param string $name 绑定的对象标识\r\n * @param mixed  $obj 绑定的对象实例\r\n * @return mixed\r\n */\r\n$request->bind($name, $obj = null);\r\n\r\n', '2017-04-28 00:21:11', '2017-04-30 21:21:33');
INSERT INTO `onethink_plugin_cheat_sheet_detail` VALUES (21, 1, '', 'Response', '/**\r\n * 构造函数\r\n * @access   public\r\n * @param mixed $data    输出数据\r\n * @param int   $code\r\n * @param array $header\r\n * @param array $options 输出参数\r\n */\r\n$response = new Response($data = \'\', $code = 200, array $header = [], $options = []);\r\n\r\n/**\r\n * 创建Response对象\r\n * @access public\r\n * @param mixed  $data    输出数据\r\n * @param string $type    输出类型\r\n * @param int    $code\r\n * @param array  $header\r\n * @param array  $options 输出参数\r\n * @return Response|JsonResponse|ViewResponse|XmlResponse|RedirectResponse|JsonpResponse\r\n */\r\nResponse::create($data = \'\', $type = \'\', $code = 200, array $header = [], $options = []);\r\n\r\n/**\r\n * 发送数据到客户端\r\n * @access public\r\n * @return mixed\r\n * @throws \\InvalidArgumentException\r\n */\r\n$response->send();\r\n\r\n/**\r\n * 处理数据\r\n * @access protected\r\n * @param mixed $data 要处理的数据\r\n * @return mixed\r\n */\r\n$this->output($data);\r\n\r\n/**\r\n * 输出的参数\r\n * @access public\r\n * @param mixed $options 输出参数\r\n * @return $this\r\n */\r\n$response->options($options = []);\r\n\r\n/**\r\n * 输出数据设置\r\n * @access public\r\n * @param mixed $data 输出数据\r\n * @return $this\r\n */\r\n$response->data($data);\r\n\r\n/**\r\n * 设置响应头\r\n * @access public\r\n * @param string|array $name  参数名\r\n * @param string       $value 参数值\r\n * @return $this\r\n */\r\n$response->header($name, $value = null);\r\n\r\n/**\r\n * 设置页面输出内容\r\n * @param $content\r\n * @return $this\r\n */\r\n$response->content($content);\r\n\r\n/**\r\n * 发送HTTP状态\r\n * @param integer $code 状态码\r\n * @return $this\r\n */\r\n$response->code($code);\r\n\r\n/**\r\n * LastModified\r\n * @param string $time\r\n * @return $this\r\n */\r\n$response->lastModified($time);\r\n\r\n/**\r\n * Expires\r\n * @param string $time\r\n * @return $this\r\n */\r\n$response->expires($time);\r\n\r\n/**\r\n * ETag\r\n * @param string $eTag\r\n * @return $this\r\n */\r\n$response->eTag($eTag);\r\n\r\n/**\r\n * 页面缓存控制\r\n * @param string $cache 状态码\r\n * @return $this\r\n */\r\n$response->cacheControl($cache);\r\n\r\n/**\r\n * 页面输出类型\r\n * @param string $contentType 输出类型\r\n * @param string $charset     输出编码\r\n * @return $this\r\n */\r\n$response->contentType($contentType, $charset = \'utf-8\');\r\n\r\n/**\r\n * 获取头部信息\r\n * @param string $name 头部名称\r\n * @return mixed\r\n */\r\n$response->getHeader($name = \'\');\r\n\r\n/**\r\n * 获取原始数据\r\n * @return mixed\r\n */\r\n$response->getData();\r\n\r\n/**\r\n * 获取输出数据\r\n * @return mixed\r\n */\r\n$response->getContent();\r\n\r\n/**\r\n * 获取状态码\r\n * @return integer\r\n */\r\n$response->getCode();', '2017-04-28 08:26:08', '2017-04-28 08:26:08');
INSERT INTO `onethink_plugin_cheat_sheet_detail` VALUES (22, 1, 'http://www.kancloud.cn/manual/thinkphp5/118029', 'Route', '/**\r\n * 注册变量规则\r\n * @access public\r\n * @param string|array  $name 变量名\r\n * @param string        $rule 变量规则\r\n * @return void\r\n */\r\nRoute::pattern($name = null, $rule = \'\');\r\n\r\n/**\r\n * 注册子域名部署规则\r\n * @access public\r\n * @param string|array  $domain 子域名\r\n * @param mixed         $rule 路由规则\r\n * @param array         $option 路由参数\r\n * @param array         $pattern 变量规则\r\n * @return void\r\n */\r\nRoute::domain($domain, $rule = \'\', $option = [], $pattern = []);\r\n\r\nself::setDomain($domain);\r\n\r\n/**\r\n * 设置路由绑定\r\n * @access public\r\n * @param mixed     $bind 绑定信息\r\n * @param string    $type 绑定类型 默认为module 支持 namespace class controller\r\n * @return mixed\r\n */\r\nRoute::bind($bind, $type = \'module\');\r\n\r\n/**\r\n * 设置或者获取路由标识\r\n * @access public\r\n * @param string|array     $name 路由命名标识 数组表示批量设置\r\n * @param array            $value 路由地址及变量信息\r\n * @return array\r\n */\r\nRoute:;name($name = \'\', $value = null);\r\n\r\n/**\r\n * 读取路由绑定\r\n * @access public\r\n * @param string    $type 绑定类型\r\n * @return mixed\r\n */\r\nRoute::getBind($type);\r\n\r\n/**\r\n * 导入配置文件的路由规则\r\n * @access public\r\n * @param array     $rule 路由规则\r\n * @param string    $type 请求类型\r\n * @return void\r\n */\r\nRoute::import(array $rule, $type = \'*\');\r\n\r\n// 批量注册路由\r\nself::registerRules($rules, $type = \'*\');\r\n\r\n/**\r\n * 注册路由规则\r\n * @access public\r\n * @param string    $rule 路由规则\r\n * @param string    $route 路由地址\r\n * @param string    $type 请求类型\r\n * @param array     $option 路由参数\r\n * @param array     $pattern 变量规则\r\n * @return void\r\n */\r\nself::rule($rule, $route = \'\', $type = \'*\', $option = [], $pattern = []);\r\n\r\n/**\r\n * 设置路由规则\r\n * @access public\r\n * @param string    $rule 路由规则\r\n * @param string    $route 路由地址\r\n * @param string    $type 请求类型\r\n * @param array     $option 路由参数\r\n * @param array     $pattern 变量规则\r\n * @param string    $group 所属分组\r\n * @return void\r\n */\r\nself::setRule($rule, $route, $type = \'*\', $option = [], $pattern = [], $group = \'\');\r\n\r\n/**\r\n * 设置当前执行的参数信息\r\n * @access public\r\n * @param array    $options 参数信息\r\n * @return mixed\r\n */\r\nself::setOption($options = []);\r\n\r\n/**\r\n * 获取当前执行的所有参数信息\r\n * @access public\r\n * @return array\r\n */\r\nRoute::getOption();\r\n\r\n/**\r\n * 获取当前的分组信息\r\n * @access public\r\n * @param string    $type 分组信息名称 name option pattern\r\n * @return mixed\r\n */\r\nRoute::getGroup($type);\r\n\r\n/**\r\n * 设置当前的路由分组\r\n * @access public\r\n * @param string    $name 分组名称\r\n * @param array     $option 分组路由参数\r\n * @param array     $pattern 分组变量规则\r\n * @return void\r\n */\r\nRoute::setGroup($name, $option = [], $pattern = []);\r\n\r\n/**\r\n * 注册路由分组\r\n * @access public\r\n * @param string|array      $name 分组名称或者参数\r\n * @param array|\\Closure    $routes 路由地址\r\n * @param array             $option 路由参数\r\n * @param array             $pattern 变量规则\r\n * @return void\r\n */\r\nRoute::group($name, $routes, $option = [], $pattern = []);\r\n\r\n/**\r\n * 注册路由\r\n * @access public\r\n * @param string    $rule 路由规则\r\n * @param string    $route 路由地址\r\n * @param array     $option 路由参数\r\n * @param array     $pattern 变量规则\r\n * @return void\r\n */\r\nRoute::any($rule, $route = \'\', $option = [], $pattern = []);\r\n\r\n/**\r\n * 注册GET路由\r\n * @access public\r\n * @param string    $rule 路由规则\r\n * @param string    $route 路由地址\r\n * @param array     $option 路由参数\r\n * @param array     $pattern 变量规则\r\n * @return void\r\n */\r\nRoute::get($rule, $route = \'\', $option = [], $pattern = []);\r\n\r\n/**\r\n * 注册POST路由\r\n * @access public\r\n * @param string    $rule 路由规则\r\n * @param string    $route 路由地址\r\n * @param array     $option 路由参数\r\n * @param array     $pattern 变量规则\r\n * @return void\r\n */\r\nRoute::post($rule, $route = \'\', $option = [], $pattern = []);\r\n\r\n/**\r\n * 注册PUT路由\r\n * @access public\r\n * @param string    $rule 路由规则\r\n * @param string    $route 路由地址\r\n * @param array     $option 路由参数\r\n * @param array     $pattern 变量规则\r\n * @return void\r\n */\r\nRoute::put($rule, $route = \'\', $option = [], $pattern = []);\r\n\r\n/**\r\n * 注册DELETE路由\r\n * @access public\r\n * @param string    $rule 路由规则\r\n * @param string    $route 路由地址\r\n * @param array     $option 路由参数\r\n * @param array     $pattern 变量规则\r\n * @return void\r\n */\r\nRoute::delete($rule, $route = \'\', $option = [], $pattern = []);\r\n\r\n/**\r\n * 注册PATCH路由\r\n * @access public\r\n * @param string    $rule 路由规则\r\n * @param string    $route 路由地址\r\n * @param array     $option 路由参数\r\n * @param array     $pattern 变量规则\r\n * @return void\r\n */\r\nRoute::patch($rule, $route = \'\', $option = [], $pattern = []);\r\n\r\n/**\r\n * 注册资源路由\r\n * @access public\r\n * @param string    $rule 路由规则\r\n * @param string    $route 路由地址\r\n * @param array     $option 路由参数\r\n * @param array     $pattern 变量规则\r\n * @return void\r\n */\r\nRoute::resource($rule, $route = \'\', $option = [], $pattern = []);\r\n\r\n/**\r\n * 注册控制器路由 操作方法对应不同的请求后缀\r\n * @access public\r\n * @param string    $rule 路由规则\r\n * @param string    $route 路由地址\r\n * @param array     $option 路由参数\r\n * @param array     $pattern 变量规则\r\n * @return void\r\n */\r\nRoute::controller($rule, $route = \'\', $option = [], $pattern = []);\r\n\r\n/**\r\n * 注册别名路由\r\n * @access public\r\n * @param string|array  $rule 路由别名\r\n * @param string        $route 路由地址\r\n * @param array         $option 路由参数\r\n * @return void\r\n */\r\nRoute::alias($rule = null, $route = \'\', $option = []);\r\n\r\n/**\r\n * 设置不同请求类型下面的方法前缀\r\n * @access public\r\n * @param string    $method 请求类型\r\n * @param string    $prefix 类型前缀\r\n * @return void\r\n */\r\nRoute::setMethodPrefix($method, $prefix = \'\');\r\n\r\n/**\r\n * rest方法定义和修改\r\n * @access public\r\n * @param string        $name 方法名称\r\n * @param array|bool    $resource 资源\r\n * @return void\r\n */\r\nRoute::rest($name, $resource = []);\r\n\r\n/**\r\n * 注册未匹配路由规则后的处理\r\n * @access public\r\n * @param string    $route 路由地址\r\n * @param string    $method 请求类型\r\n * @param array     $option 路由参数\r\n * @return void\r\n */\r\nRoute::miss($route, $method = \'*\', $option = []);\r\n\r\n/**\r\n * 注册一个自动解析的URL路由\r\n * @access public\r\n * @param string    $route 路由地址\r\n * @return void\r\n */\r\nRoute::function auto($route);\r\n\r\n/**\r\n * 获取或者批量设置路由定义\r\n * @access public\r\n * @param mixed $rules 请求类型或者路由定义数组\r\n * @return array\r\n */\r\nRoute::rules($rules = \'\');\r\n\r\n/**\r\n * 检测子域名部署\r\n * @access public\r\n * @param Request   $request Request请求对象\r\n * @param array     $currentRules 当前路由规则\r\n * @param string    $method 请求类型\r\n * @return void\r\n */\r\nRoute::checkDomain($request, &$currentRules, $method = \'get\');\r\n\r\n/**\r\n * 检测URL路由\r\n * @access public\r\n * @param Request   $request Request请求对象\r\n * @param string    $url URL地址\r\n * @param string    $depr URL分隔符\r\n * @param bool      $checkDomain 是否检测域名规则\r\n * @return false|array\r\n */\r\nRoute::check($request, $url, $depr = \'/\', $checkDomain = false);\r\n\r\nself::getRouteExpress($key);\r\n\r\n/**\r\n * 检测路由规则\r\n * @access private\r\n * @param Request   $request\r\n * @param array     $rules 路由规则\r\n * @param string    $url URL地址\r\n * @param string    $depr URL分割符\r\n * @param string    $group 路由分组名\r\n * @param array     $options 路由参数（分组）\r\n * @return mixed\r\n */\r\nself::checkRoute($request, $rules, $url, $depr = \'/\', $group = \'\', $options = []);\r\n\r\n/**\r\n * 检测路由别名\r\n * @access private\r\n * @param Request   $request\r\n * @param string    $url URL地址\r\n * @param string    $depr URL分隔符\r\n * @return mixed\r\n */\r\nself::checkRouteAlias($request, $url, $depr);\r\n\r\n/**\r\n * 检测URL绑定\r\n * @access private\r\n * @param string    $url URL地址\r\n * @param array     $rules 路由规则\r\n * @param string    $depr URL分隔符\r\n * @return mixed\r\n */\r\nself::checkUrlBind(&$url, &$rules, $depr = \'/\');\r\n\r\n/**\r\n * 绑定到类\r\n * @access public\r\n * @param string    $url URL地址\r\n * @param string    $class 类名（带命名空间）\r\n * @param string    $depr URL分隔符\r\n * @return array\r\n */\r\nRoute::bindToClass($url, $class, $depr = \'/\');\r\n\r\n/**\r\n * 绑定到命名空间\r\n * @access public\r\n * @param string    $url URL地址\r\n * @param string    $namespace 命名空间\r\n * @param string    $depr URL分隔符\r\n * @return array\r\n */\r\nRoute::bindToNamespace($url, $namespace, $depr = \'/\');\r\n\r\n/**\r\n * 绑定到控制器类\r\n * @access public\r\n * @param string    $url URL地址\r\n * @param string    $controller 控制器名 （支持带模块名 index/user ）\r\n * @param string    $depr URL分隔符\r\n * @return array\r\n */\r\nRoute::bindToController($url, $controller, $depr = \'/\');\r\n\r\n/**\r\n * 绑定到模块/控制器\r\n * @access public\r\n * @param string    $url URL地址\r\n * @param string    $controller 控制器类名（带命名空间）\r\n * @param string    $depr URL分隔符\r\n * @return array\r\n */\r\nRoute::bindToModule($url, $controller, $depr = \'/\');\r\n\r\n/**\r\n * 路由参数有效性检查\r\n * @access private\r\n * @param array     $option 路由参数\r\n * @param Request   $request Request对象\r\n * @return bool\r\n */\r\nself::checkOption($option, $request);\r\n\r\n/**\r\n * 检测路由规则\r\n * @access private\r\n * @param string    $rule 路由规则\r\n * @param string    $route 路由地址\r\n * @param string    $url URL地址\r\n * @param array     $pattern 变量规则\r\n * @param array     $option 路由参数\r\n * @param string    $depr URL分隔符（全局）\r\n * @return array|false\r\n */\r\nself::checkRule($rule, $route, $url, $pattern, $option, $depr);\r\n\r\n/**\r\n * 解析模块的URL地址 [模块/控制器/操作?]参数1=值1&参数2=值2...\r\n * @access public\r\n * @param string    $url URL地址\r\n * @param string    $depr URL分隔符\r\n * @param bool      $autoSearch 是否自动深度搜索控制器\r\n * @return array\r\n */\r\nRoute::parseUrl($url, $depr = \'/\', $autoSearch = false);\r\n\r\n/**\r\n * 解析URL的pathinfo参数和变量\r\n * @access private\r\n * @param string    $url URL地址\r\n * @return array\r\n */\r\nself::parseUrlPath($url);\r\n\r\n/**\r\n * 检测URL和规则路由是否匹配\r\n * @access private\r\n * @param string    $url URL地址\r\n * @param string    $rule 路由规则\r\n * @param array     $pattern 变量规则\r\n * @return array|false\r\n */\r\nself::match($url, $rule, $pattern);\r\n\r\n/**\r\n * 解析规则路由\r\n * @access private\r\n * @param string    $rule 路由规则\r\n * @param string    $route 路由地址\r\n * @param string    $pathinfo URL地址\r\n * @param array     $option 路由参数\r\n * @param array     $matches 匹配的变量\r\n * @return array\r\n */\r\nself::parseRule($rule, $route, $pathinfo, $option = [], $matches = []);\r\n\r\n/**\r\n * 解析URL地址为 模块/控制器/操作\r\n * @access private\r\n * @param string    $url URL地址\r\n * @return array\r\n */\r\nself::parseModule($url);\r\n\r\n/**\r\n * 解析URL地址中的参数Request对象\r\n * @access private\r\n * @param string    $rule 路由规则\r\n * @param array     $var 变量\r\n * @return void\r\n */\r\nself::parseUrlParams($url, &$var = []);\r\n\r\n// 分析路由规则中的变量\r\nself::parseVar($rule);', '2017-04-28 08:43:10', '2017-04-30 21:21:58');
INSERT INTO `onethink_plugin_cheat_sheet_detail` VALUES (23, 1, 'http://www.kancloud.cn/manual/thinkphp5/126166', 'Session', '// 设置或者获取session作用域（前缀）\r\nSession::prefix($prefix = \'\');\r\n\r\n/**\r\n * session初始化\r\n * @param array $config\r\n * @return void\r\n * @throws \\think\\Exception\r\n */\r\nSession::init(array $config = []);\r\n\r\n// session自动启动或者初始化\r\nSession::boot();\r\n\r\n/**\r\n * session设置\r\n * @param string        $name session名称\r\n * @param mixed         $value session值\r\n * @param string|null   $prefix 作用域（前缀）\r\n * @return void\r\n */\r\nSession::set($name, $value = \'\', $prefix = null);\r\n\r\n/**\r\n * session获取\r\n * @param string        $name session名称\r\n * @param string|null   $prefix 作用域（前缀）\r\n * @return mixed\r\n */\r\nSession::get($name = \'\', $prefix = null);\r\n\r\n/**\r\n * session获取并删除\r\n * @param string        $name session名称\r\n * @param string|null   $prefix 作用域（前缀）\r\n * @return mixed\r\n */\r\nSession::pull($name, $prefix = null);\r\n\r\n/**\r\n * session设置 下一次请求有效\r\n * @param string        $name session名称\r\n * @param mixed         $value session值\r\n * @param string|null   $prefix 作用域（前缀）\r\n * @return void\r\n */\r\nSession::flash($name, $value);\r\n\r\n// 清空当前请求的session数据\r\nSession::function flush();\r\n\r\n/**\r\n * 删除session数据\r\n * @param string|array  $name session名称\r\n * @param string|null   $prefix 作用域（前缀）\r\n * @return void\r\n */\r\nSession::delete($name, $prefix = null);\r\n\r\n/**\r\n * 清空session数据\r\n * @param string|null   $prefix 作用域（前缀）\r\n * @return void\r\n */\r\nSession::clear($prefix = null);\r\n\r\n/**\r\n * 判断session数据\r\n * @param string        $name session名称\r\n * @param string|null   $prefix\r\n * @return bool\r\n */\r\nSession::has($name, $prefix = null);\r\n\r\n/**\r\n * 添加数据到一个session数组\r\n * @param  string  $key\r\n * @param  mixed   $value\r\n * @return void\r\n */\r\nSession::push($key, $value);\r\n\r\n/**\r\n * 启动session\r\n * @return void\r\n */\r\nSession::start();\r\n\r\n/**\r\n * 销毁session\r\n * @return void\r\n */\r\nSession::destroy();\r\n\r\n/**\r\n * 重新生成session_id\r\n * @param bool $delete 是否删除关联会话文件\r\n * @return void\r\n */\r\nSession::regenerate($delete = false);\r\n\r\n/**\r\n * 暂停session\r\n * @return void\r\n */\r\nSession::pause();', '2017-04-30 17:13:39', '2017-04-30 21:22:37');
INSERT INTO `onethink_plugin_cheat_sheet_detail` VALUES (24, 1, 'http://www.kancloud.cn/manual/thinkphp5/118122', 'Template', '// 构造函数\r\n$template = new Template(array $config = []);\r\n\r\n\r\n/**\r\n * 字符串替换 避免正则混淆\r\n * @access private\r\n * @param string $str\r\n * @return string\r\n */\r\n$this->stripPreg($str);\r\n\r\n/**\r\n * 模板变量赋值\r\n * @access public\r\n * @param mixed $name\r\n * @param mixed $value\r\n * @return void\r\n */\r\n$template->assign($name, $value = \'\');\r\n\r\n/**\r\n * 模板引擎参数赋值\r\n * @access public\r\n * @param mixed $name\r\n * @param mixed $value\r\n */\r\n$template->__set($name, $value);\r\n\r\n/**\r\n * 模板引擎配置项\r\n * @access public\r\n * @param array|string $config\r\n * @return void|array\r\n */\r\n$template->config($config);\r\n\r\n/**\r\n * 模板变量获取\r\n * @access public\r\n * @param  string $name 变量名\r\n * @return mixed\r\n */\r\n$template->get($name = \'\');\r\n\r\n/**\r\n * 渲染模板文件\r\n * @access public\r\n * @param string    $template 模板文件\r\n * @param array     $vars 模板变量\r\n * @param array     $config 模板参数\r\n * @return void\r\n */\r\n$template->fetch($template, $vars = [], $config = []);\r\n\r\n/**\r\n * 渲染模板内容\r\n * @access public\r\n * @param string    $content 模板内容\r\n * @param array     $vars 模板变量\r\n * @param array     $config 模板参数\r\n * @return void\r\n */\r\n$template->display($content, $vars = [], $config = []);\r\n\r\n/**\r\n * 设置布局\r\n * @access public\r\n * @param mixed     $name 布局模板名称 false 则关闭布局\r\n * @param string    $replace 布局模板内容替换标识\r\n * @return object\r\n */\r\n$template->layout($name, $replace = \'\');\r\n\r\n/**\r\n * 检查编译缓存是否有效\r\n * 如果无效则需要重新编译\r\n * @access private\r\n * @param string $cacheFile 缓存文件名\r\n * @return boolean\r\n */\r\n$this->checkCache($cacheFile);\r\n\r\n/**\r\n * 检查编译缓存是否存在\r\n * @access public\r\n * @param string $cacheId 缓存的id\r\n * @return boolean\r\n */\r\n$template->isCache($cacheId);\r\n\r\n/**\r\n * 编译模板文件内容\r\n * @access private\r\n * @param string    $content 模板内容\r\n * @param string    $cacheFile 缓存文件名\r\n * @return void\r\n */\r\n$this->compiler(&$content, $cacheFile);\r\n\r\n/**\r\n * 模板解析入口\r\n * 支持普通标签和TagLib解析 支持自定义标签库\r\n * @access public\r\n * @param string $content 要解析的模板内容\r\n * @return void\r\n */\r\n$template->parse(&$content);\r\n\r\n/**\r\n * 检查PHP语法\r\n * @access private\r\n * @param string $content 要解析的模板内容\r\n * @return void\r\n * @throws \\think\\Exception\r\n */\r\n$this->parsePhp(&$content);\r\n\r\n/**\r\n * 解析模板中的布局标签\r\n * @access private\r\n * @param string $content 要解析的模板内容\r\n * @return void\r\n */\r\n$this->parseLayout(&$content);\r\n\r\n/**\r\n * 解析模板中的include标签\r\n * @access private\r\n * @param  string $content 要解析的模板内容\r\n * @return void\r\n */\r\n$this->parseInclude(&$content);\r\n\r\n/**\r\n * 解析模板中的extend标签\r\n * @access private\r\n * @param  string $content 要解析的模板内容\r\n * @return void\r\n */\r\n$this->parseExtend(&$content);\r\n\r\n/**\r\n * 替换页面中的literal标签\r\n * @access private\r\n * @param  string   $content 模板内容\r\n * @param  boolean  $restore 是否为还原\r\n * @return void\r\n */\r\n$this->parseLiteral(&$content, $restore = false);\r\n\r\n/**\r\n * 获取模板中的block标签\r\n * @access private\r\n * @param  string   $content 模板内容\r\n * @param  boolean  $sort 是否排序\r\n * @return array\r\n */\r\n$this->parseBlock(&$content, $sort = false);\r\n\r\n/**\r\n * 搜索模板页面中包含的TagLib库\r\n * 并返回列表\r\n * @access private\r\n * @param  string $content 模板内容\r\n * @return array|null\r\n */\r\n$this->getIncludeTagLib(&$content);\r\n\r\n/**\r\n * TagLib库解析\r\n * @access public\r\n * @param  string   $tagLib 要解析的标签库\r\n * @param  string   $content 要解析的模板内容\r\n * @param  boolean  $hide 是否隐藏标签库前缀\r\n * @return void\r\n */\r\n$template->parseTagLib($tagLib, &$content, $hide = false);\r\n\r\n/**\r\n * 分析标签属性\r\n * @access public\r\n * @param  string   $str 属性字符串\r\n * @param  string   $name 不为空时返回指定的属性名\r\n * @return array\r\n */\r\n$template->parseAttr($str, $name = null);\r\n\r\n/**\r\n * 模板标签解析\r\n * 格式： {TagName:args [|content] }\r\n * @access private\r\n * @param  string $content 要解析的模板内容\r\n * @return void\r\n */\r\n$this->parseTag(&$content);\r\n\r\n/**\r\n * 模板变量解析,支持使用函数\r\n * 格式： {$varname|function1|function2=arg1,arg2}\r\n * @access public\r\n * @param  string $varStr 变量数据\r\n * @return void\r\n */\r\n$template->parseVar(&$varStr);\r\n\r\n/**\r\n * 对模板中使用了函数的变量进行解析\r\n * 格式 {$varname|function1|function2=arg1,arg2}\r\n * @access public\r\n * @param  string $varStr 变量字符串\r\n * @return void\r\n */\r\n$template->parseVarFunction(&$varStr);\r\n\r\n/**\r\n * 特殊模板变量解析\r\n * 格式 以 $Think. 打头的变量属于特殊模板变量\r\n * @access public\r\n * @param  array $vars 变量数组\r\n * @return string\r\n */\r\n$template->parseThinkVar($vars);\r\n\r\n/**\r\n * 分析加载的模板文件并读取内容 支持多个模板文件读取\r\n * @access private\r\n * @param  string $templateName 模板文件名\r\n * @return string\r\n */\r\n$this->parseTemplateName($templateName);\r\n\r\n/**\r\n * 解析模板文件名\r\n * @access private\r\n * @param  string $template 文件名\r\n * @return string|false\r\n */\r\n$this->parseTemplateFile($template);\r\n\r\n/**\r\n * 按标签生成正则\r\n * @access private\r\n * @param  string $tagName 标签名\r\n * @return string\r\n */\r\n$this->getRegex($tagName);', '2017-04-30 17:24:19', '2017-04-30 21:22:58');
INSERT INTO `onethink_plugin_cheat_sheet_detail` VALUES (25, 1, 'http://www.kancloud.cn/manual/thinkphp5/118012', 'Url', '/**\r\n * URL生成 支持路由反射\r\n * @param string            $url 路由地址\r\n * @param string|array      $vars 参数（支持数组和字符串）a=val&b=val2... [\'a\'=>\'val1\', \'b\'=>\'val2\']\r\n * @param string|bool       $suffix 伪静态后缀，默认为true表示获取配置值\r\n * @param boolean|string    $domain 是否显示域名 或者直接传入域名\r\n * @return string\r\n */\r\nUrl::build($url = \'\', $vars = \'\', $suffix = true, $domain = false);\r\n\r\n// 直接解析URL地址\r\nself::parseUrl($url, &$domain);\r\n{\r\n    $request = Request::instance();\r\n    if (0 === strpos($url, \'/\')) {\r\n        // 直接作为路由地址解析\r\n        $url = substr($url, 1);\r\n    } elseif (false !== strpos($url, \'\\\\\')) {\r\n        // 解析到类\r\n        $url = ltrim(str_replace(\'\\\\\', \'/\', $url), \'/\');\r\n    } elseif (0 === strpos($url, \'@\')) {\r\n        // 解析到控制器\r\n        $url = substr($url, 1);\r\n    } else {\r\n        // 解析到 模块/控制器/操作\r\n        $module  = $request->module();\r\n        $domains = Route::rules(\'domain\');\r\n        if (true === $domain && 2 == substr_count($url, \'/\')) {\r\n            $current = $request->host();\r\n            $match   = [];\r\n            $pos     = [];\r\n            foreach ($domains as $key => $item) {\r\n                if (isset($item[\'[bind]\']) && 0 === strpos($url, $item[\'[bind]\'][0])) {\r\n                    $pos[$key] = strlen($item[\'[bind]\'][0]) + 1;\r\n                    $match[]   = $key;\r\n                    $module    = \'\';\r\n                }\r\n            }\r\n            if ($match) {\r\n                $domain = current($match);\r\n                foreach ($match as $item) {\r\n                    if (0 === strpos($current, $item)) {\r\n                        $domain = $item;\r\n                    }\r\n                }\r\n                self::$bindCheck = true;\r\n                $url             = substr($url, $pos[$domain]);\r\n            }\r\n        } elseif ($domain) {\r\n            if (isset($domains[$domain][\'[bind]\'][0])) {\r\n                $bindModule = $domains[$domain][\'[bind]\'][0];\r\n                if ($bindModule && !in_array($bindModule[0], [\'\\\\\', \'@\'])) {\r\n                    $module = \'\';\r\n                }\r\n            }\r\n        }\r\n        $module = $module ? $module . \'/\' : \'\';\r\n\r\n        $controller = Loader::parseName($request->controller());\r\n        if (\'\' == $url) {\r\n            // 空字符串输出当前的 模块/控制器/操作\r\n            $url = $module . $controller . \'/\' . $request->action();\r\n        } else {\r\n            $path       = explode(\'/\', $url);\r\n            $action     = Config::get(\'url_convert\') ? strtolower(array_pop($path)) : array_pop($path);\r\n            $controller = empty($path) ? $controller : (Config::get(\'url_convert\') ? Loader::parseName(array_pop($path)) : array_pop($path));\r\n            $module     = empty($path) ? $module : array_pop($path) . \'/\';\r\n            $url        = $module . $controller . \'/\' . $action;\r\n        }\r\n    }\r\n    return $url;\r\n}\r\n\r\n// 检测域名\r\nself::parseDomain(&$url, $domain);\r\n// 解析URL后缀\r\nself::parseSuffix($suffix);\r\n\r\n// 匹配路由地址\r\nUrl::function getRuleUrl($rule, &$vars = []);\r\n\r\n// 指定当前生成URL地址的root\r\nUrl::function root($root);', '2017-04-30 17:37:58', '2017-04-30 21:24:07');
INSERT INTO `onethink_plugin_cheat_sheet_detail` VALUES (26, 1, 'http://www.kancloud.cn/manual/thinkphp5/129319', 'Validate', '/**\r\n * 构造函数\r\n * @access public\r\n * @param array $rules 验证规则\r\n * @param array $message 验证提示信息\r\n * @param array $field 验证字段描述信息\r\n */\r\n$validate = new Validate(array $rules = [], $message = [], $field = []);\r\n\r\n/**\r\n * 实例化验证\r\n * @access public\r\n * @param array     $rules 验证规则\r\n * @param array     $message 验证提示信息\r\n * @param array     $field 验证字段描述信息\r\n * @return Validate\r\n */\r\nValidate::make($rules = [], $message = [], $field = []);\r\n\r\n/**\r\n * 添加字段验证规则\r\n * @access protected\r\n * @param string|array  $name  字段名称或者规则数组\r\n * @param mixed         $rule  验证规则\r\n * @return Validate\r\n */\r\n$validate->rule($name, $rule = \'\');\r\n\r\n/**\r\n * 注册验证（类型）规则\r\n * @access public\r\n * @param string    $type  验证规则类型\r\n * @param mixed     $callback callback方法(或闭包)\r\n * @return void\r\n */\r\nValidate::extend($type, $callback = null);\r\n\r\n/**\r\n * 获取验证规则的默认提示信息\r\n * @access protected\r\n * @param string|array  $type  验证规则类型名称或者数组\r\n * @param string        $msg  验证提示信息\r\n * @return void\r\n */\r\nValidate::setTypeMsg($type, $msg = null);\r\n\r\n/**\r\n * 设置提示信息\r\n * @access public\r\n * @param string|array  $name  字段名称\r\n * @param string        $message 提示信息\r\n * @return Validate\r\n */\r\n$validate->message($name, $message = \'\');\r\n\r\n/**\r\n * 设置验证场景\r\n * @access public\r\n * @param string|array  $name  场景名或者场景设置数组\r\n * @param mixed         $fields 要验证的字段\r\n * @return Validate\r\n */\r\n$validate->scene($name, $fields = null);\r\n\r\n/**\r\n * 判断是否存在某个验证场景\r\n * @access public\r\n * @param string $name 场景名\r\n * @return bool\r\n */\r\n$validate->hasScene($name);\r\n\r\n/**\r\n * 设置批量验证\r\n * @access public\r\n * @param bool $batch  是否批量验证\r\n * @return Validate\r\n */\r\n$validate->batch($batch = true);\r\n\r\n/**\r\n * 数据自动验证\r\n * @access public\r\n * @param array     $data  数据\r\n * @param mixed     $rules  验证规则\r\n * @param string    $scene 验证场景\r\n * @return bool\r\n */\r\n$validate->check($data, $rules = [], $scene = \'\');\r\n\r\n/**\r\n * 验证单个字段规则\r\n * @access protected\r\n * @param string    $field  字段名\r\n * @param mixed     $value  字段值\r\n * @param mixed     $rules  验证规则\r\n * @param array     $data  数据\r\n * @param string    $title  字段描述\r\n * @param array     $msg  提示信息\r\n * @return mixed\r\n */\r\n$this->checkItem($field, $value, $rules, $data, $title = \'\', $msg = []);\r\n\r\n/**\r\n * 验证是否和某个字段的值一致\r\n * @access protected\r\n * @param mixed     $value 字段值\r\n * @param mixed     $rule  验证规则\r\n * @param array     $data  数据\r\n * @param string    $field 字段名\r\n * @return bool\r\n */\r\n$this->confirm($value, $rule, $data, $field = \'\');\r\n\r\n/**\r\n * 验证是否和某个字段的值是否不同\r\n * @access protected\r\n * @param mixed $value 字段值\r\n * @param mixed $rule  验证规则\r\n * @param array $data  数据\r\n * @return bool\r\n */\r\n$this->different($value, $rule, $data);\r\n\r\n/**\r\n * 验证是否大于等于某个值\r\n * @access protected\r\n * @param mixed     $value  字段值\r\n * @param mixed     $rule  验证规则\r\n * @return bool\r\n */\r\n$this->egt($value, $rule);\r\n\r\n/**\r\n * 验证是否大于某个值\r\n * @access protected\r\n * @param mixed     $value  字段值\r\n * @param mixed     $rule  验证规则\r\n * @return bool\r\n */\r\n$this->gt($value, $rule);\r\n\r\n/**\r\n * 验证是否小于等于某个值\r\n * @access protected\r\n * @param mixed     $value  字段值\r\n * @param mixed     $rule  验证规则\r\n * @return bool\r\n */\r\n$this->elt($value, $rule);\r\n\r\n/**\r\n * 验证是否小于某个值\r\n * @access protected\r\n * @param mixed     $value  字段值\r\n * @param mixed     $rule  验证规则\r\n * @return bool\r\n */\r\n$this->lt($value, $rule);\r\n\r\n/**\r\n * 验证是否等于某个值\r\n * @access protected\r\n * @param mixed     $value  字段值\r\n * @param mixed     $rule  验证规则\r\n * @return bool\r\n */\r\n$this->eq($value, $rule);\r\n\r\n/**\r\n * 验证字段值是否为有效格式\r\n * @access protected\r\n * @param mixed     $value  字段值\r\n * @param string    $rule  验证规则\r\n * @param array     $data  验证数据\r\n * @return bool\r\n */\r\n$this->is($value, $rule, $data = []);\r\n\r\n// 判断图像类型\r\n$this->getImageType($image);\r\n\r\n/**\r\n * 验证是否为合格的域名或者IP 支持A，MX，NS，SOA，PTR，CNAME，AAAA，A6， SRV，NAPTR，TXT 或者 ANY类型\r\n * @access protected\r\n * @param mixed     $value  字段值\r\n * @param mixed     $rule  验证规则\r\n * @return bool\r\n */\r\n$this->activeUrl($value, $rule);\r\n\r\n/**\r\n * 验证是否有效IP\r\n * @access protected\r\n * @param mixed     $value  字段值\r\n * @param mixed     $rule  验证规则 ipv4 ipv6\r\n * @return bool\r\n */\r\n$this->ip($value, $rule);\r\n\r\n/**\r\n * 验证上传文件后缀\r\n * @access protected\r\n * @param mixed     $file  上传文件\r\n * @param mixed     $rule  验证规则\r\n * @return bool\r\n */\r\n$this->fileExt($file, $rule);\r\n\r\n/**\r\n * 验证上传文件类型\r\n * @access protected\r\n * @param mixed     $file  上传文件\r\n * @param mixed     $rule  验证规则\r\n * @return bool\r\n */\r\n$this->fileMime($file, $rule);\r\n\r\n/**\r\n * 验证上传文件大小\r\n * @access protected\r\n * @param mixed     $file  上传文件\r\n * @param mixed     $rule  验证规则\r\n * @return bool\r\n */\r\n$this->fileSize($file, $rule);\r\n\r\n/**\r\n * 验证图片的宽高及类型\r\n * @access protected\r\n * @param mixed     $file  上传文件\r\n * @param mixed     $rule  验证规则\r\n * @return bool\r\n */\r\n$this->image($file, $rule);\r\n\r\n/**\r\n * 验证请求类型\r\n * @access protected\r\n * @param mixed     $value  字段值\r\n * @param mixed     $rule  验证规则\r\n * @return bool\r\n */\r\n$this->method($value, $rule);\r\n\r\n/**\r\n * 验证时间和日期是否符合指定格式\r\n * @access protected\r\n * @param mixed     $value  字段值\r\n * @param mixed     $rule  验证规则\r\n * @return bool\r\n */\r\n$this->dateFormat($value, $rule);\r\n\r\n/**\r\n * 验证是否唯一\r\n * @access protected\r\n * @param mixed     $value  字段值\r\n * @param mixed     $rule  验证规则 格式：数据表,字段名,排除ID,主键名\r\n * @param array     $data  数据\r\n * @param string    $field  验证字段名\r\n * @return bool\r\n */\r\n$this->unique($value, $rule, $data, $field);\r\n\r\n/**\r\n * 使用行为类验证\r\n * @access protected\r\n * @param mixed     $value  字段值\r\n * @param mixed     $rule  验证规则\r\n * @param array     $data  数据\r\n * @return mixed\r\n */\r\n$this->behavior($value, $rule, $data);\r\n\r\n/**\r\n * 使用filter_var方式验证\r\n * @access protected\r\n * @param mixed     $value  字段值\r\n * @param mixed     $rule  验证规则\r\n * @return bool\r\n */\r\n$this->filter($value, $rule);\r\n\r\n/**\r\n * 验证某个字段等于某个值的时候必须\r\n * @access protected\r\n * @param mixed     $value  字段值\r\n * @param mixed     $rule  验证规则\r\n * @param array     $data  数据\r\n * @return bool\r\n */\r\n$this->requireIf($value, $rule, $data);\r\n\r\n/**\r\n * 通过回调方法验证某个字段是否必须\r\n * @access protected\r\n * @param mixed     $value  字段值\r\n * @param mixed     $rule  验证规则\r\n * @param array     $data  数据\r\n * @return bool\r\n */\r\n$this->requireCallback($value, $rule, $data);\r\n\r\n/**\r\n * 验证某个字段有值的情况下必须\r\n * @access protected\r\n * @param mixed     $value  字段值\r\n * @param mixed     $rule  验证规则\r\n * @param array     $data  数据\r\n * @return bool\r\n */\r\n$this->requireWith($value, $rule, $data);\r\n\r\n/**\r\n * 验证是否在范围内\r\n * @access protected\r\n * @param mixed     $value  字段值\r\n * @param mixed     $rule  验证规则\r\n * @return bool\r\n */\r\n$this->in($value, $rule);\r\n\r\n/**\r\n * 验证是否不在某个范围\r\n * @access protected\r\n * @param mixed     $value  字段值\r\n * @param mixed     $rule  验证规则\r\n * @return bool\r\n */\r\n$this->notIn($value, $rule);\r\n\r\n/**\r\n * between验证数据\r\n * @access protected\r\n * @param mixed     $value  字段值\r\n * @param mixed     $rule  验证规则\r\n * @return bool\r\n */\r\n$this->between($value, $rule);\r\n\r\n/**\r\n * 使用notbetween验证数据\r\n * @access protected\r\n * @param mixed     $value  字段值\r\n * @param mixed     $rule  验证规则\r\n * @return bool\r\n */\r\n$this->notBetween($value, $rule);\r\n\r\n/**\r\n * 验证数据长度\r\n * @access protected\r\n * @param mixed     $value  字段值\r\n * @param mixed     $rule  验证规则\r\n * @return bool\r\n */\r\n$this->length($value, $rule);\r\n\r\n/**\r\n * 验证数据最大长度\r\n * @access protected\r\n * @param mixed     $value  字段值\r\n * @param mixed     $rule  验证规则\r\n * @return bool\r\n */\r\n$this->max($value, $rule);\r\n\r\n/**\r\n * 验证数据最小长度\r\n * @access protected\r\n * @param mixed     $value  字段值\r\n * @param mixed     $rule  验证规则\r\n * @return bool\r\n */\r\n$this->min($value, $rule);\r\n\r\n/**\r\n * 验证日期\r\n * @access protected\r\n * @param mixed     $value  字段值\r\n * @param mixed     $rule  验证规则\r\n * @return bool\r\n */\r\n$this->after($value, $rule);\r\n\r\n/**\r\n * 验证日期\r\n * @access protected\r\n * @param mixed     $value  字段值\r\n * @param mixed     $rule  验证规则\r\n * @return bool\r\n */\r\n$this->before($value, $rule);\r\n\r\n/**\r\n * 验证有效期\r\n * @access protected\r\n * @param mixed     $value  字段值\r\n * @param mixed     $rule  验证规则\r\n * @return bool\r\n */\r\n$this->expire($value, $rule);\r\n\r\n/**\r\n * 验证IP许可\r\n * @access protected\r\n * @param string    $value  字段值\r\n * @param mixed     $rule  验证规则\r\n * @return mixed\r\n */\r\n$this->allowIp($value, $rule);\r\n\r\n/**\r\n * 验证IP禁用\r\n * @access protected\r\n * @param string    $value  字段值\r\n * @param mixed     $rule  验证规则\r\n * @return mixed\r\n */\r\n$this->denyIp($value, $rule);\r\n\r\n/**\r\n * 使用正则验证数据\r\n * @access protected\r\n * @param mixed     $value  字段值\r\n * @param mixed     $rule  验证规则 正则规则或者预定义正则名\r\n * @return mixed\r\n */\r\n$this->regex($value, $rule);\r\n\r\n/**\r\n * 验证表单令牌\r\n * @access protected\r\n * @param mixed     $value  字段值\r\n * @param mixed     $rule  验证规则\r\n * @param array     $data  数据\r\n * @return bool\r\n */\r\n$this->token($value, $rule, $data);\r\n\r\n// 获取错误信息\r\n$validate->getError();\r\n\r\n/**\r\n * 获取数据值\r\n * @access protected\r\n * @param array     $data  数据\r\n * @param string    $key  数据标识 支持二维\r\n * @return mixed\r\n */\r\n$this->getDataValue($data, $key);\r\n\r\n/**\r\n * 获取验证规则的错误提示信息\r\n * @access protected\r\n * @param string    $attribute  字段英文名\r\n * @param string    $title  字段描述名\r\n * @param string    $type  验证规则名称\r\n * @param mixed     $rule  验证规则数据\r\n * @return string\r\n */\r\n$this->getRuleMsg($attribute, $title, $type, $rule);\r\n\r\n/**\r\n * 获取数据验证的场景\r\n * @access protected\r\n * @param string $scene  验证场景\r\n * @return array\r\n */\r\n$this->getScene($scene = \'\');\r\n\r\nValidate::__callStatic($method, $params);', '2017-04-30 18:09:31', '2017-04-30 21:24:29');
INSERT INTO `onethink_plugin_cheat_sheet_detail` VALUES (27, 1, 'http://www.kancloud.cn/manual/thinkphp5/118112', 'View', '/**\r\n * 构造函数\r\n * @access public\r\n * @param array $engine  模板引擎参数\r\n * @param array $replace  字符串替换参数\r\n */\r\n$view = New View($engine = [], $replace = []);\r\n\r\n/**\r\n * 初始化视图\r\n * @access public\r\n * @param array $engine  模板引擎参数\r\n * @param array $replace  字符串替换参数\r\n * @return object\r\n */\r\nView::instance($engine = [], $replace = []);\r\n\r\n/**\r\n * 模板变量静态赋值\r\n * @access public\r\n * @param mixed $name  变量名\r\n * @param mixed $value 变量值\r\n * @return void\r\n */\r\nView::share($name, $value = \'\');\r\n\r\n/**\r\n * 模板变量赋值\r\n * @access public\r\n * @param mixed $name  变量名\r\n * @param mixed $value 变量值\r\n * @return $this\r\n */\r\n$view->assign($name, $value = \'\');\r\n\r\n/**\r\n * 设置当前模板解析的引擎\r\n * @access public\r\n * @param array|string $options 引擎参数\r\n * @return $this\r\n */\r\n$view->engine($options = []);\r\n\r\n/**\r\n * 配置模板引擎\r\n * @access private\r\n * @param string|array  $name 参数名\r\n * @param mixed         $value 参数值\r\n * @return void\r\n */\r\n$view->config($name, $value = null);\r\n\r\n/**\r\n * 解析和获取模板内容 用于输出\r\n * @param string    $template 模板文件名或者内容\r\n * @param array     $vars     模板输出变量\r\n * @param array     $replace 替换内容\r\n * @param array     $config     模板参数\r\n * @param bool      $renderContent     是否渲染内容\r\n * @return string\r\n * @throws Exception\r\n */\r\n$view->fetch($template = \'\', $vars = [], $replace = [], $config = [], $renderContent = false);\r\n\r\n/**\r\n * 视图内容替换\r\n * @access public\r\n * @param string|array  $content 被替换内容（支持批量替换）\r\n * @param string        $replace    替换内容\r\n * @return $this\r\n */\r\n$view->replace($content, $replace = \'\');\r\n\r\n/**\r\n * 渲染内容输出\r\n * @access public\r\n * @param string $content 内容\r\n * @param array  $vars    模板输出变量\r\n * @param array  $replace 替换内容\r\n * @param array  $config     模板参数\r\n * @return mixed\r\n */\r\n$view->display($content, $vars = [], $replace = [], $config = []);\r\n\r\n/**\r\n * 模板变量赋值\r\n * @access public\r\n * @param string    $name  变量名\r\n * @param mixed     $value 变量值\r\n */\r\n$view->__set($name, $value);\r\n\r\n/**\r\n * 取得模板显示变量的值\r\n * @access protected\r\n * @param string $name 模板变量\r\n * @return mixed\r\n */\r\n$view->__get($name);\r\n\r\n/**\r\n * 检测模板变量是否设置\r\n * @access public\r\n * @param string $name 模板变量名\r\n * @return bool\r\n */\r\n$view->__isset($name);  ', '2017-04-30 18:19:11', '2017-04-30 21:24:50');
