INSERT INTO `onethink_plugin_movie` VALUES (3866308, '埃莉与拉鲁的奇异冒险', NULL, 'http://movie.douban.com/subject/3866308/', 'null', '', '1987', 'movie', NULL, 0, 0, '　　Приключения Элли и Рару новое Грузия, СССР, 1987 г. 93 мин.\n                                    \n                                　　Таинственный незнакомец дарит директору зоопарка пони Рару, утверждая, что тот может творить чудеса. Однако в скрытый потенциал скромной лошадки верит лишь девочка Элли. Взяв пони под свое покровительство, она отправляется вместе с ним на поиски прекрасной певицы Наяны в надежде, что та сможет вернуть Рару волшебный дар', 0);
