INSERT INTO `onethink_plugin_movie` VALUES (4910200, '死神剧场版BLEACH 地狱篇', NULL, 'http://movie.douban.com/subject/4910200/', 'a:3:{s:5:\"small\";s:13:\"s_4910200.jpg\";s:6:\"medium\";s:13:\"m_4910200.jpg\";s:5:\"large\";s:13:\"l_4910200.jpg\";}', '5.9', '2010', 'movie', 1320854400, 0, 1, '　　本次《地狱篇》将由原作者久保带人老师亲自主控，而另一个让人振奋的消息则是，在本作剧场版中，将会出现在原作里给人以强烈印象的完全虚化版一护。这是完全虚化的一护首次在剧场版上登场演出，再加上久保老师的剧本保证，相信这支剧场版定会取得不错的成绩。', 0);
