INSERT INTO `onethink_plugin_movie` VALUES (6957283, '爪哇火线', NULL, 'http://movie.douban.com/subject/6957283/', 'null', '5.0', '2013', 'movie', NULL, 0, 0, '　　一个神秘的美国团队在一名穆斯林警 察帮助下追查一名危险的国际珠宝窃贼，这名窃贼绑架了苏丹的女儿意图盗取皇室的宝藏。影片取景广泛，片中我们可以欣赏到古代的宫殿、清真寺、庙宇、地下迷宫和金字塔等异国风情，当然还有一个惊天阴谋正在酝酿。', 0);
