INSERT INTO `onethink_plugin_movie` VALUES (26575103, '捉妖记2', NULL, 'http://movie.douban.com/subject/26575103/', 'null', '', '2017', 'movie', NULL, 0, 0, '　　据投资人江志强透露：《捉妖记》续拍已经在计划之中，计划将其打造成一部像《哈利波特》这样的系列电影。而井柏然白百何等主演已经达成了未来三部曲的合作意向。欢呼吧各位胡巴的萝卜丝们！', 0);
