INSERT INTO `onethink_plugin_timeline` VALUES (37, '发布一个tp5的微信编辑器wechat_msg_editor', '2017,03,21', '2017,03,21', 25, 'jay', '', '<p>微信公众号图文编辑器，在公司和前端处理图文消息预览和实际发送不一致问题，研究开发出此工具，基于tp5，codemirror，ueditor、vue和CssToInlineStyles<br></p><p></p><p>直接将css+html 转换为html和行内样式，方便发布时复制，并且提供了预览</p><p><br></p>');
INSERT INTO `onethink_plugin_timeline` VALUES (12, 'osx 上dash程序员文档利器加入ThinkPHP', '2013,3,10', '2013,3,10', 11, 'Jay', '', '<p><a href=\"http://www.thinkphp.cn/Uploads/editor/2013-03-10/513c0e20892b6.png\" target=\"_blank\" style=\"color: rgb(114, 185, 57); text-decoration: underline; font-family: \'Century Gothic\', \'Microsoft yahei\'; line-height: 28px; white-space: normal;\"><img src=\"http://www.thinkphp.cn/Uploads/editor/2013-03-10/513c0e20892b6.png\" style=\"margin: 10px 0px; padding: 0px; max-width: 688px;\"/></a><br style=\"color: rgb(50, 50, 50); font-family: \'Century Gothic\', \'Microsoft yahei\'; line-height: 28px; white-space: normal;\"/><span style=\"color: rgb(50, 50, 50); font-family: \'Century Gothic\', \'Microsoft yahei\'; line-height: 28px;\">如图，在程序Preference里的Downloads里添加</span><br style=\"color: rgb(50, 50, 50); font-family: \'Century Gothic\', \'Microsoft yahei\'; line-height: 28px; white-space: normal;\"/><span style=\"color: rgb(50, 50, 50); font-family: \'Century Gothic\', \'Microsoft yahei\'; line-height: 28px;\">http://baelabs.duapp.com/ThinkPHP.xml 这个源就可以添加ThinkPHP最新手册了</span><br style=\"color: rgb(50, 50, 50); font-family: \'Century Gothic\', \'Microsoft yahei\'; line-height: 28px; white-space: normal;\"/><span style=\"color: rgb(50, 50, 50); font-family: \'Century Gothic\', \'Microsoft yahei\'; line-height: 28px;\">以后也可以自动更新的。</span></p>');
