INSERT INTO `onethink_plugin_timeline` VALUES (13, 'ThinkPHP环境探针', '2013,4,02', '2013,4,02', 12, 'Jay', '', '<p><span style=\"color: rgb(50, 50, 50); font-family: \'Century Gothic\', \'Microsoft yahei\'; line-height: 28px;\">改自nette框架的环境检测</span><br style=\"color: rgb(50, 50, 50); font-family: \'Century Gothic\', \'Microsoft yahei\'; line-height: 28px; white-space: normal;\"/><span style=\"color: rgb(50, 50, 50); font-family: \'Century Gothic\', \'Microsoft yahei\'; line-height: 28px;\">写了几个我认为要检测的，大家觉得有其他需要的可以更改checker.php中的数组。</span><br style=\"color: rgb(50, 50, 50); font-family: \'Century Gothic\', \'Microsoft yahei\'; line-height: 28px; white-space: normal;\"/><span style=\"color: rgb(50, 50, 50); font-family: \'Century Gothic\', \'Microsoft yahei\'; line-height: 28px;\">效果如下图：</span></p><p><span style=\"color: rgb(50, 50, 50); font-family: \'Century Gothic\', \'Microsoft yahei\'; line-height: 28px;\"><img src=\"http://www.thinkphp.cn/Public/Images/extension/common.gif\" width=\"16\" height=\"16\" border=\"0\" alt=\"附件\" align=\"absmiddle\" style=\"margin: 10px 0px; padding: 0px; max-width: 688px; color: rgb(50, 50, 50); font-family: \'Century Gothic\', \'Microsoft yahei\'; white-space: normal;\"/><span style=\"color: rgb(50, 50, 50); font-family: \'Century Gothic\', \'Microsoft yahei\';\"> </span><a href=\"http://www.thinkphp.cn/code/download/id/158.html\" title=\"下载\" style=\"color: rgb(114, 185, 57); font-family: \'Century Gothic\', \'Microsoft yahei\'; white-space: normal;\">Requirements-Checker.zip</a><span style=\"color: rgb(50, 50, 50); font-family: \'Century Gothic\', \'Microsoft yahei\';\"> </span><span class=\"date\" style=\"opacity: 0.4; margin-right: 18px; color: rgb(50, 50, 50); font-family: \'Century Gothic\', \'Microsoft yahei\';\">( 23.83 KB 下载：333 次 )</span></span></p>');
