INSERT INTO `onethink_plugin_timeline` VALUES (25, '成为SAE高级开发者', '2014,2,20', '2014,2,20', 41, '917647288@qq.com', '', '<ul class=\"data-show\" style=\"font-family:\'Helvetica Neue\', Helvetica, Arial, sans-serif;background-color:#FFFFFF;\">\r\n <li class=\"require\" style=\"color:#333333;font-size:14px;\">\r\n    <h6 style=\"font-family:inherit;color:#44B3E9;font-size:11.9px;\">\r\n      申请条件:\r\n   </h6>\r\n </li>\r\n <ol style=\"color:#333333;font-size:14px;\">\r\n    <li style=\"color:#919090;font-size:12px;\">\r\n      符合以下条件：&nbsp;<br />\r\n&nbsp;&nbsp;a) 知名博客 在技术圈有较高的影响力 我们会根据您的博客的技术性以及有无知名博客参与回复 有无知名博客外链您的博客等方面综合评定你的博客 如果您有其他可以证明您的博客影响力的内容 如微博关注数等请在备注中注明<br />\r\n&nbsp;&nbsp;b) 知名互联网企业员工(<a href=\"http://sae.sina.com.cn/?m=home&amp;a=devlevel#CMD:IT-COMPANIES\">SAE认可知名IT公司清单</a>)&nbsp;<br />\r\n&nbsp;&nbsp;c) 开源项目创建者 开源项目的创建者 项目已在生产环境中使用 项目持续维护超1年并且保持活跃<br />\r\n   </li>\r\n   <li style=\"color:#919090;font-size:12px;\">\r\n      若非开源项目创建者 须提交原创应用 标准同中级开发者原创应用标准\r\n    </li>\r\n </ol>\r\n <li>\r\n    <span><span style=\"color:#44B3E9;font-size:14px;line-height:1.5;\">【高级】开发者</span></span> \r\n  </li>\r\n</ul>\r\n<span style=\"background-color:#FFFFFF;font-family:\'Helvetica Neue\', Helvetica, Arial, sans-serif;line-height:1.5;\"> \r\n<ol style=\"color:#333333;font-family:\'Helvetica Neue\', Helvetica, Arial, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n  <ol>\r\n    <li style=\"text-align:left;color:#919090;font-size:12px;\">\r\n      每月免费补充至1.5万云豆\r\n   </li>\r\n   <li style=\"text-align:left;color:#919090;font-size:12px;\">\r\n      <span style=\"line-height:1.5;\">一次原创应用推广机会&nbsp;</span><span style=\"line-height:1.5;\">价值: 1800元/年&nbsp;</span><a href=\"http://sae.sina.com.cn/?m=home&amp;a=devlevel&amp;help=1\">详情</a>\r\n    </li>\r\n </ol>\r\n</ol>\r\n</span>');
